import React, { useState } from 'react';
import {
  VStack,
  HStack,
  Text,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  FormControl,
  FormLabel,
  Card,
  CardBody,
  SimpleGrid,
  Alert,
  AlertIcon,
  Box,
} from '@chakra-ui/react';
import { calculateBMI } from '../../utils/calorieCalculator';

interface BodyMetricsProps {
  height: number;
  weight: number;
  age: number;
  onMetricsChange: (metrics: { height: number; weight: number; age: number }) => void;
}

const BodyMetrics: React.FC<BodyMetricsProps> = ({
  height,
  weight,
  age,
  onMetricsChange,
}) => {
  const [localHeight, setLocalHeight] = useState(height);
  const [localWeight, setLocalWeight] = useState(weight);
  const [localAge, setLocalAge] = useState(age);

  const handleChange = (field: 'height' | 'weight' | 'age', value: number) => {
    const newMetrics = {
      height: field === 'height' ? value : localHeight,
      weight: field === 'weight' ? value : localWeight,
      age: field === 'age' ? value : localAge,
    };

    if (field === 'height') setLocalHeight(value);
    if (field === 'weight') setLocalWeight(value);
    if (field === 'age') setLocalAge(value);

    onMetricsChange(newMetrics);
  };

  // BMI Í≥ÑÏÇ∞ Î∞è ÌëúÏãú
  const bmiData = localHeight > 0 && localWeight > 0 
    ? calculateBMI(localWeight, localHeight) 
    : null;

  const getBMIColor = (category: string): 'info' | 'success' | 'warning' | 'error' => {
    switch (category) {
      case 'underweight': return 'info';
      case 'normal': return 'success';
      case 'overweight': return 'warning';
      case 'obese': return 'error';
      default: return 'info';
    }
  };

  return (
    <VStack spacing={6} align="stretch">
      <VStack spacing={2} textAlign="center">
        <Text fontSize="4xl">üìè</Text>
        <Text fontSize="2xl" fontWeight="bold" color="gray.800">
          Î™∏Ïóê ÎåÄÌï¥ ÏïåÎ†§Ï£ºÏÑ∏Ïöî
        </Text>
        <Text color="gray.600">
          Ï†ïÌôïÌïú ÏπºÎ°úÎ¶¨ Í≥ÑÏÇ∞ÏùÑ ÏúÑÌï¥ ÌïÑÏöîÌïú Ï†ïÎ≥¥ÏòàÏöî
        </Text>
      </VStack>

      <Card>
        <CardBody>
          <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6}>
            {/* ÌÇ§ ÏûÖÎ†• */}
            <FormControl>
              <FormLabel fontWeight="bold" color="teal.700">
                üßç‚Äç‚ôÇÔ∏è ÌÇ§
              </FormLabel>
              <HStack spacing={2}>
                <NumberInput
                  value={localHeight}
                  onChange={(_, value) => handleChange('height', isNaN(value) ? 0 : value)}
                  min={100}
                  max={250}
                  step={1}
                  size="lg"
                >
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>
                <Text fontSize="lg" color="gray.600" minW="30px">
                  cm
                </Text>
              </HStack>
            </FormControl>

            {/* Î™∏Î¨¥Í≤å ÏûÖÎ†• */}
            <FormControl>
              <FormLabel fontWeight="bold" color="teal.700">
                ‚öñÔ∏è Î™∏Î¨¥Í≤å
              </FormLabel>
              <HStack spacing={2}>
                <NumberInput
                  value={localWeight}
                  onChange={(_, value) => handleChange('weight', isNaN(value) ? 0 : value)}
                  min={30}
                  max={200}
                  step={0.1}
                  precision={1}
                  size="lg"
                >
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>
                <Text fontSize="lg" color="gray.600" minW="30px">
                  kg
                </Text>
              </HStack>
            </FormControl>

            {/* ÎÇòÏù¥ ÏûÖÎ†• */}
            <FormControl>
              <FormLabel fontWeight="bold" color="teal.700">
                üéÇ ÎÇòÏù¥
              </FormLabel>
              <HStack spacing={2}>
                <NumberInput
                  value={localAge}
                  onChange={(_, value) => handleChange('age', isNaN(value) ? 0 : value)}
                  min={15}
                  max={100}
                  step={1}
                  size="lg"
                >
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>
                <Text fontSize="lg" color="gray.600" minW="30px">
                  ÏÑ∏
                </Text>
              </HStack>
            </FormControl>
          </SimpleGrid>
        </CardBody>
      </Card>

      {/* BMI ÌëúÏãú */}
      {bmiData && (
        <Alert
          status={getBMIColor(bmiData.category)}
          borderRadius="md"
        >
          <AlertIcon />
          <Box>
            <Text fontWeight="bold">
              BMI: {bmiData.bmi} ({bmiData.description})
            </Text>
            <Text fontSize="sm">
              {bmiData.category === 'normal' 
                ? 'Í±¥Í∞ïÌïú Ï≤¥Ï§ë Î≤îÏúÑÏóê ÏûàÏñ¥Ïöî! üëç'
                : bmiData.category === 'underweight'
                ? 'Ï≤¥Ï§ëÏù¥ Ï°∞Í∏à Î∂ÄÏ°±Ìï¥Ïöî. Í∑†ÌòïÏû°Ìûå ÏãùÎã®ÏúºÎ°ú Í±¥Í∞ïÌïú Ï≤¥Ï§ëÏùÑ ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî.'
                : bmiData.category === 'overweight'
                ? 'ÏïΩÍ∞ÑÏùò Ï≤¥Ï§ë Í∞êÎüâÏù¥ ÎèÑÏõÄÏù¥ Îê† Í≤É Í∞ôÏïÑÏöî.'
                : 'Í±¥Í∞ïÌïú Ï≤¥Ï§ë Í∞êÎüâÏùÑ ÌÜµÌï¥ Îçî Í±¥Í∞ïÌï¥Ïßà Ïàò ÏûàÏñ¥Ïöî.'
              }
            </Text>
          </Box>
        </Alert>
      )}

      <Box bg="gray.50" p={4} borderRadius="md" border="1px" borderColor="gray.200">
        <Text fontSize="sm" color="gray.600" textAlign="center">
          üí° ÏûÖÎ†•ÌïòÏã† Ï†ïÎ≥¥Îäî Í≥ºÌïôÏ†ÅÏù∏ Í≥µÏãùÏùÑ Î∞îÌÉïÏúºÎ°ú Í∞úÏù∏ ÎßûÏ∂§ ÏπºÎ°úÎ¶¨Î•º Í≥ÑÏÇ∞ÌïòÎäîÎç∞ ÏÇ¨Ïö©ÎèºÏöî.
        </Text>
      </Box>
    </VStack>
  );
};

export default BodyMetrics;
