import React, { useState } from 'react';
import {
  VStack,
  HStack,
  Text,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  Card,
  CardBody,
  SimpleGrid,
  useColorModeValue,
  Box,
  Badge,
  Alert,
  AlertIcon,
  Button,
  Divider,
} from '@chakra-ui/react';

interface BudgetSelectionProps {
  selectedBudget: number;
  onBudgetSelect: (budget: number) => void;
}

const BudgetSelection: React.FC<BudgetSelectionProps> = ({
  selectedBudget,
  onBudgetSelect,
}) => {
  const [customBudget, setCustomBudget] = useState(selectedBudget);
  const [useCustom, setUseCustom] = useState(false);

  const selectedBg = useColorModeValue('teal.50', 'teal.900');
  const selectedBorder = useColorModeValue('teal.500', 'teal.300');
  const hoverBg = useColorModeValue('gray.50', 'gray.700');

  // Ï∂îÏ≤ú ÏòàÏÇ∞ ÏòµÏÖòÎì§
  const budgetOptions = [
    {
      value: 50000,
      label: '5ÎßåÏõê',
      subtitle: 'Ï†àÏïΩÌòï',
      description: 'Í∏∞Î≥∏Ï†ÅÏù∏ ÏãùÏû¨Î£å Ï§ëÏã¨Ïùò ÏïåÎú∞Ìïú ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['ÏåÄ, Îã¨Í±Ä, Îã≠Í∞ÄÏä¥ÏÇ¥', 'Í∏∞Î≥∏ Ï±ÑÏÜåÎ•ò', 'Í∞ÑÎã®Ìïú Ï°∞Î¶¨ÏãùÌíà'],
      color: 'blue',
      recommended: false,
    },
    {
      value: 70000,
      label: '7ÎßåÏõê',
      subtitle: 'ÌëúÏ§ÄÌòï',
      description: 'ÏòÅÏñë Í∑†ÌòïÏùÑ Í≥†Î†§Ìïú Ï†ÅÎãπÌïú ÏòàÏÇ∞Ïùò ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['Îã§ÏñëÌïú Îã®Î∞±ÏßàÏõê', 'Ïã†ÏÑ†Ìïú Ï±ÑÏÜå/Í≥ºÏùº', 'ÏòÅÏñë Î≥¥Ï°∞ÏãùÌíà'],
      color: 'green',
      recommended: true,
    },
    {
      value: 100000,
      label: '10ÎßåÏõê',
      subtitle: 'ÌíçÏÑ±Ìòï',
      description: 'ÌîÑÎ¶¨ÎØ∏ÏóÑ ÏãùÏû¨Î£åÎ°ú Íµ¨ÏÑ±Ìïú ÌíçÎ∂ÄÌïú ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['Í≥†Í∏â Îã®Î∞±Ïßà', 'ÏàòÏûÖ Í≥ºÏùº', 'Í±¥Í∞ï Í∏∞Îä•ÏãùÌíà'],
      color: 'orange',
      recommended: false,
    },
    {
      value: 120000,
      label: '12ÎßåÏõê',
      subtitle: 'ÌîÑÎ¶¨ÎØ∏ÏóÑ',
      description: 'ÏµúÍ≥†Í∏â ÏãùÏû¨Î£åÏôÄ Í±¥Í∞ïÏãùÌíàÏúºÎ°ú Íµ¨ÏÑ±Ìïú ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['ÌîÑÎ¶¨ÎØ∏ÏóÑ ÏãùÏû¨Î£å', 'Ïò§Í∞ÄÎãâ Ï†úÌíà', 'Í≥†Í∏â Í±¥Í∞ïÏãùÌíà'],
      color: 'purple',
      recommended: false,
    },
  ];

  const handlePresetSelect = (budget: number) => {
    setUseCustom(false);
    setCustomBudget(budget);
    onBudgetSelect(budget);
  };

  const handleCustomBudgetChange = (value: number) => {
    setCustomBudget(value);
    if (useCustom) {
      onBudgetSelect(value);
    }
  };

  const handleCustomToggle = () => {
    const newUseCustom = !useCustom;
    setUseCustom(newUseCustom);
    if (newUseCustom) {
      onBudgetSelect(customBudget);
    }
  };

  const getBudgetAdvice = (budget: number) => {
    if (budget < 40000) {
      return { type: 'warning', message: 'ÏòàÏÇ∞Ïù¥ ÎÑàÎ¨¥ Ï†ÅÏñ¥Ïöî. Ï∂©Î∂ÑÌïú ÏòÅÏñë Í≥µÍ∏âÏù¥ Ïñ¥Î†§Ïö∏ Ïàò ÏûàÏñ¥Ïöî.' };
    } else if (budget < 50000) {
      return { type: 'info', message: 'ÏïåÎú∞Ìïú ÏòàÏÇ∞Ïù¥ÎÑ§Ïöî! Í∏∞Î≥∏Ï†ÅÏù∏ ÏòÅÏñëÏÜå ÏúÑÏ£ºÎ°ú Ï∂îÏ≤úÌï¥ÎìúÎ¶¥Í≤åÏöî.' };
    } else if (budget <= 100000) {
      return { type: 'success', message: 'Ï†ÅÎãπÌïú ÏòàÏÇ∞Ïù¥ÏóêÏöî! Í∑†ÌòïÏû°Ìûå ÏùºÏ£ºÏùº ÏãùÎã®ÏùÑ Íµ¨ÏÑ±Ìï† Ïàò ÏûàÏñ¥Ïöî.' };
    } else if (budget <= 150000) {
      return { type: 'info', message: 'Ïó¨Ïú†Î°úÏö¥ ÏòàÏÇ∞Ïù¥ÎÑ§Ïöî! ÌîÑÎ¶¨ÎØ∏ÏóÑ ÏãùÏû¨Î£åÎèÑ Ìè¨Ìï®Ìï† Ïàò ÏûàÏñ¥Ïöî.' };
    } else {
      return { type: 'warning', message: 'ÏòàÏÇ∞Ïù¥ ÎÜíÏïÑÏöî. Îçî Ìö®Ïú®Ï†ÅÏù∏ ÏãùÎã® Íµ¨ÏÑ±ÏùÑ Í∂åÏû•ÎìúÎ†§Ïöî.' };
    }
  };

  const advice = getBudgetAdvice(useCustom ? customBudget : selectedBudget);
  const currentBudget = useCustom ? customBudget : selectedBudget;

  return (
    <VStack spacing={6} align="stretch">
      <VStack spacing={2} textAlign="center">
        <Text fontSize="4xl">üí∞</Text>
        <Text fontSize="2xl" fontWeight="bold" color="gray.800">
          Ïù¥Î≤àÏ£º Ïû•Î≥¥Í∏∞ ÏòàÏÇ∞ÏùÑ ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî
        </Text>
        <Text color="gray.600">
          ÏùºÏ£ºÏùº ÏòàÏÇ∞Ïóê ÎßûÎäî ÏµúÏ†ÅÏùò ÏãùÎã®ÏùÑ Ï∂îÏ≤úÌï¥ÎìúÎ†§Ïöî
        </Text>
      </VStack>

      {/* Ï∂îÏ≤ú ÏòàÏÇ∞ ÏòµÏÖòÎì§ */}
      <SimpleGrid columns={{ base: 1, md: 2 }} spacing={4}>
        {budgetOptions.map((option) => (
          <Card
            key={option.value}
            cursor="pointer"
            onClick={() => handlePresetSelect(option.value)}
            bg={selectedBudget === option.value && !useCustom ? selectedBg : 'white'}
            borderWidth={selectedBudget === option.value && !useCustom ? '2px' : '1px'}
            borderColor={selectedBudget === option.value && !useCustom ? selectedBorder : 'gray.200'}
            _hover={{
              bg: selectedBudget === option.value && !useCustom ? selectedBg : hoverBg,
              transform: 'translateY(-2px)',
              shadow: 'lg',
            }}
            transition="all 0.2s"
            position="relative"
          >
            {option.recommended && (
              <Badge
                position="absolute"
                top={-2}
                right={-2}
                colorScheme="teal"
                fontSize="xs"
                px={2}
                py={1}
                borderRadius="full"
              >
                ‚ú® Ï∂îÏ≤ú
              </Badge>
            )}
            <CardBody p={5}>
              <VStack spacing={3} align="stretch">
                {/* Ìó§Îçî */}
                <HStack justify="space-between">
                  <VStack align="start" spacing={1}>
                    <Text fontSize="xl" fontWeight="bold">
                      {option.label}
                    </Text>
                    <Badge colorScheme={option.color} size="sm">
                      {option.subtitle}
                    </Badge>
                  </VStack>
                </HStack>

                {/* ÏÑ§Î™Ö */}
                <Text fontSize="sm" color="gray.600" lineHeight="1.4">
                  {option.description}
                </Text>

                {/* ÌäπÏßï */}
                <VStack spacing={1} align="stretch">
                  <Text fontSize="xs" fontWeight="bold" color="gray.700">
                    Ìè¨Ìï® Ìï≠Î™©:
                  </Text>
                  {option.features.map((feature, index) => (
                    <HStack key={index} spacing={2}>
                      <Text fontSize="xs" color={`${option.color}.500`}>
                        ‚Ä¢
                      </Text>
                      <Text fontSize="xs" color="gray.600">
                        {feature}
                      </Text>
                    </HStack>
                  ))}
                </VStack>
              </VStack>
            </CardBody>
          </Card>
        ))}
      </SimpleGrid>

      <Divider />

      {/* ÏßÅÏ†ë ÏûÖÎ†• ÏòµÏÖò */}
      <Card>
        <CardBody>
          <VStack spacing={4} align="stretch">
            <HStack justify="space-between">
              <VStack align="start" spacing={1}>
                <Text fontWeight="bold">ÏßÅÏ†ë ÏòàÏÇ∞ ÏÑ§Ï†ïÌïòÍ∏∞</Text>
                <Text fontSize="sm" color="gray.600">
                  ÏõêÌïòÎäî ÏòàÏÇ∞ÏùÑ ÏßÅÏ†ë ÏûÖÎ†•Ìï† Ïàò ÏûàÏñ¥Ïöî
                </Text>
              </VStack>
              <Button
                size="sm"
                variant={useCustom ? "solid" : "outline"}
                colorScheme="teal"
                onClick={handleCustomToggle}
              >
                {useCustom ? "ÏÇ¨Ïö© Ï§ë" : "ÏÇ¨Ïö©ÌïòÍ∏∞"}
              </Button>
            </HStack>

            {useCustom && (
              <HStack spacing={4}>
                <NumberInput
                  value={customBudget}
                  onChange={(_, value) => handleCustomBudgetChange(isNaN(value) ? 0 : value)}
                  min={30000}
                  max={200000}
                  step={5000}
                  size="lg"
                  flex={1}
                >
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>
                <Text fontSize="lg" color="gray.600" minW="30px">
                  Ïõê
                </Text>
              </HStack>
            )}
          </VStack>
        </CardBody>
      </Card>

      {/* ÏòàÏÇ∞ Ï°∞Ïñ∏ */}
      <Alert
        status={advice.type as any}
        borderRadius="md"
      >
        <AlertIcon />
        <Box>
          <Text fontSize="sm">
            <strong>üí° ÏòàÏÇ∞ Î∂ÑÏÑù:</strong> {advice.message}
          </Text>
        </Box>
      </Alert>

      {/* ÏòàÏÇ∞Î≥Ñ Í∂åÏû•ÏÇ¨Ìï≠ */}
      <Box bg="blue.50" p={4} borderRadius="md" border="1px" borderColor="blue.200">
        <Text fontSize="sm" color="blue.800" textAlign="center">
          üí∞ <strong>ÌòÑÏû¨ ÏÑ§Ï†ï: {currentBudget.toLocaleString()}Ïõê</strong>
          <br />
          1Ïù∏ Í∏∞Ï§Ä Ìïú Îã¨ ÏãùÎπÑÎ°ú {currentBudget >= 300000 ? 'ÎÑâÎÑâÌïú' : currentBudget >= 200000 ? 'Ï†ÅÎãπÌïú' : 'Ï†àÏïΩÌòï'} ÏòàÏÇ∞Ïù¥ÏóêÏöî.
        </Text>
      </Box>
    </VStack>
  );
};

export default BudgetSelection;
