import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  VStack,
  HStack,
  Text,
  Button,
  Card,
  CardBody,
  Heading,
  Image,
  Badge,
  useToast,
  Collapse,
  Icon,
} from '@chakra-ui/react';
import { ArrowBackIcon, ExternalLinkIcon, TimeIcon, InfoIcon, ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import { useAppContext } from '../context/AppContext';
import { CoupangProduct, Recipe } from '../types';
import { getRecipesByGoal, getRecipeIngredients, recipeLoader } from '../data/recipeData';
import { calculateRecipeFrequency } from '../utils/personalizedRecommendation';
import { debugRecipeMapping } from '../utils/recipeDebugger';

import Layout from './Layout/Layout';

const RecommendationScreen: React.FC = () => {
  const navigate = useNavigate();
  const toast = useToast();
  const {
    userInput,
    recommendationResult,
    setSelectedProduct,
    userProfile // ÏÇ¨Ïö©Ïûê Î™©Ìëú Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  } = useAppContext();

  // üç≥ ÏÇ¨Ïö©Ïûê Î™©ÌëúÏóê ÎßûÎäî Î†àÏãúÌîº Í∞ÄÏ†∏Ïò§Í∏∞ (ÎπÑÎèôÍ∏∞) - hooksÎ•º Ï°∞Í±¥Î∂Ä Î¶¨ÌÑ¥ Ï†ÑÏóê ÏÑ†Ïñ∏
  const [recommendedRecipes, setRecommendedRecipes] = useState<Recipe[]>([]);
  const [isLoadingRecipes, setIsLoadingRecipes] = useState(true);
  const [recipeStats, setRecipeStats] = useState<any>(null);

  useEffect(() => {
    // Ï∂îÏ≤ú Í≤∞Í≥ºÍ∞Ä ÏóÜÏúºÎ©¥ Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏
    if (!recommendationResult) {
      toast({
        title: "Ï∂îÏ≤ú Í≤∞Í≥ºÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§",
        description: "Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú ÎèåÏïÑÍ∞ÄÏÑú Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      navigate('/');
      return;
    }

    const loadRecipes = async () => {
      setIsLoadingRecipes(true);
      try {
        // üîç ÎîîÎ≤ÑÍπÖ Ïã§Ìñâ
        console.log('üîç === Î†àÏãúÌîº Îß§Ìïë ÎîîÎ≤ÑÍπÖ ÏãúÏûë ===');
        debugRecipeMapping();

        // Ï∫êÏãú ÏßÑÎã®
        console.log('üîç === Ï∫êÏãú ÏßÑÎã® ÏãúÏûë ===');
        await recipeLoader.diagnoseCacheStatus();

        // ÏÇ¨Ïö©Ïûê Î™©Ìëú ÌôïÏù∏ Î∞è ÏÑ§Ï†ï
        const userGoal = userProfile.goal || 'maintenance';
        console.log('üéØ ÏÇ¨Ïö©Ïûê Î™©Ìëú:', userGoal);

        // Î™©ÌëúÍ∞Ä ÏóÜÏúºÎ©¥ Í∏∞Î≥∏Í∞íÏúºÎ°ú ÏÑ§Ï†ï
        if (!userProfile.goal) {
          console.log('‚ö†Ô∏è ÏÇ¨Ïö©Ïûê Î™©ÌëúÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏùå, Í∏∞Î≥∏Í∞í(maintenance)ÏúºÎ°ú ÏÑ§Ï†ï');
        }

        // Î†àÏãúÌîº Î°úÎìú
        const recipes = await getRecipesByGoal(userGoal);
        console.log('üìã Î°úÎìúÎêú Î†àÏãúÌîº Ïàò:', recipes.length);
        console.log('üìã Î†àÏãúÌîº IDÎì§:', recipes.map(r => r.id));

        if (recipes.length === 0) {
          console.error('‚ö†Ô∏è Î†àÏãúÌîºÍ∞Ä ÌïòÎÇòÎèÑ Î°úÎìúÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§!');
          console.log('üí° CSV ÌååÏùºÏù¥ Ï†úÎåÄÎ°ú Î°úÎìúÎêòÎäîÏßÄ ÎÑ§Ìä∏ÏõåÌÅ¨ ÌÉ≠ÏóêÏÑú ÌôïÏù∏ÌïòÏÑ∏Ïöî');
          console.log('üí° Î∏åÎùºÏö∞Ï†Ä ÏΩòÏÜîÏóêÏÑú ÏóêÎü¨ Î©îÏãúÏßÄÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî');

          // ÌÜµÍ≥Ñ Ï†ïÎ≥¥Î°ú CSV Î°úÎìú Ïó¨Î∂Ä ÌôïÏù∏
          const stats = await recipeLoader.getStatistics();
          console.log('üìä CSV ÌÜµÍ≥Ñ:', stats);
          if (stats.totalRecipes === 0) {
            console.error('‚ùå CSV ÌååÏùºÏù¥ Î°úÎìúÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§!');
          }
        }

        setRecommendedRecipes(recipes);

        // ÌÜµÍ≥Ñ Ï†ïÎ≥¥ Î°úÎìú
        const stats = await recipeLoader.getStatistics();
        setRecipeStats(stats);
      } catch (error) {
        console.error('‚ùå Î†àÏãúÌîº Î°úÎìú Ïã§Ìå®:', error);

        // ÏóêÎü¨ ÏÉÅÏÑ∏ Ï†ïÎ≥¥
        if (error instanceof Error) {
          console.error('ÏóêÎü¨ Î©îÏãúÏßÄ:', error.message);
          console.error('ÏóêÎü¨ Ïä§ÌÉù:', error.stack);
        }

        // Îπà Î∞∞Ïó¥Î°ú ÏÑ§Ï†ïÌïòÏó¨ UI ÌÅ¨ÎûòÏãú Î∞©ÏßÄ
        setRecommendedRecipes([]);
      } finally {
        setIsLoadingRecipes(false);
      }
    };

    loadRecipes();
  }, [userProfile.goal]);

  // recommendationResultÍ∞Ä ÏóÜÏúºÎ©¥ Î°úÎî© ÏÉÅÌÉú ÌëúÏãú
  if (!recommendationResult) {
    return (
      <Layout>
        <Container maxW="container.xl" py={8}>
          <Text textAlign="center">Î°úÎî© Ï§ë...</Text>
        </Container>
      </Layout>
    );
  }

  const { 
    totalBudgetUsed, 
    budgetRemaining, 
    nutritionBalance, 
    message, 
    nutritionTargets,
    userInsights 
  } = recommendationResult;

  // ÏãúÍ∞ÑÎåÄÎ≥Ñ Î†àÏãúÌîº Î∂ÑÎ•ò
  const mealTypeRecipes = {
    breakfast: recommendedRecipes.filter(r => r.mealType === 'breakfast'),
    lunch: recommendedRecipes.filter(r => r.mealType === 'lunch'),
    dinner: recommendedRecipes.filter(r => r.mealType === 'dinner'),
  };

  // ÏÉÅÌíà ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleProductClick = (product: CoupangProduct) => {
    setSelectedProduct(product);
    navigate(`/product/${product.id}`);
  };

  // Îã§Ïãú ÏûÖÎ†•ÌïòÍ∏∞ Ìï∏Îì§Îü¨
  const handleBackToInput = () => {
    navigate('/');
  };

  const budgetUsagePercentage = (totalBudgetUsed / userInput.budget) * 100;

  // üé® Ïª¥Ìå©Ìä∏ Î†àÏãúÌîº Ïπ¥Îìú Ïª¥Ìè¨ÎÑåÌä∏
  const CompactRecipeCard: React.FC<{ recipe: Recipe }> = ({ recipe }) => {
    const [isIngredientsOpen, setIsIngredientsOpen] = useState(false);
    const ingredients = getRecipeIngredients(recipe.id);
    const totalPrice = ingredients.reduce((sum: number, ing: any) => sum + ing.product.price, 0);
    const toastForBulkPurchase = useToast();

    // üõí Ï†ÑÏ≤¥ Íµ¨Îß§ Ìï∏Îì§Îü¨
    const handleBulkPurchase = (ingredients: any[]) => {

      // ÏÇ¨Ïö©ÏûêÏóêÍ≤å ÌôïÏù∏ Î©îÏãúÏßÄ
      const confirmMessage = `${ingredients.length}Í∞úÏùò Ïû¨Î£åÎ•º Î™®Îëê Íµ¨Îß§ÌïòÏãúÍ≤†ÏäµÎãàÍπå?\nÏ¥ù ÏòàÏÉÅ ÎπÑÏö©: ${totalPrice.toLocaleString()}Ïõê\n\nÍ∞Å Ïû¨Î£åÏùò Ïø†Ìå° ÌéòÏù¥ÏßÄÍ∞Ä ÏÉà ÌÉ≠ÏúºÎ°ú Ïó¥Î¶ΩÎãàÎã§.`;

      if (window.confirm(confirmMessage)) {
        // 500ms Í∞ÑÍ≤©ÏúºÎ°ú ÏàúÏ∞®Ï†ÅÏúºÎ°ú ÏÉà ÌÉ≠ Ïó¥Í∏∞ (Î∏åÎùºÏö∞Ï†Ä ÌåùÏóÖ Ï∞®Îã® Î∞©ÏßÄ)
        ingredients.forEach((ingredient, index) => {
          setTimeout(() => {
            window.open(ingredient.product.coupangUrl, '_blank');
          }, index * 500);
        });

        // ÏÑ±Í≥µ ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ
        toastForBulkPurchase({
          title: 'Ï†ÑÏ≤¥ Íµ¨Îß§ ÌéòÏù¥ÏßÄ Ïó¥Í∏∞ ÏôÑÎ£å',
          description: `${ingredients.length}Í∞ú Ïû¨Î£åÏùò Ïø†Ìå° ÌéòÏù¥ÏßÄÍ∞Ä ÏÉà ÌÉ≠ÏúºÎ°ú Ïó¥Î†∏ÏäµÎãàÎã§.`,
          status: 'success',
          duration: 5000,
          isClosable: true,
        });

        // Î∂ÑÏÑùÏùÑ ÏúÑÌïú Ïù¥Î≤§Ìä∏ Î°úÍπÖ (ÏÑ†ÌÉùÏÇ¨Ìï≠)
        console.log('Bulk purchase initiated:', {
          recipeId: recipe.id,
          recipeName: recipe.name,
          totalPrice,
          ingredientCount: ingredients.length,
          ingredients: ingredients.map(ing => ({
            name: ing.product.name,
            price: ing.product.price,
            quantity: ing.quantity
          }))
        });
      }
    };

    // üçΩÔ∏è Î†àÏãúÌîº Ï†úÏ°∞ ÎπàÎèÑ Î∂ÑÏÑù
    const recipeFrequency = calculateRecipeFrequency(recipe.id);

    // üßÆ ÏòÅÏñëÏÜå Í≥ÑÏÇ∞ (Ïû¨Î£åÎ≥Ñ ÏòÅÏñëÏÜå √ó ÏÇ¨Ïö©Îüâ)
    const totalNutrition = ingredients.reduce((total: any, ing: any) => {
      const product = ing.product;
      const ratio = ing.quantity / 100; // 100g Í∏∞Ï§ÄÏúºÎ°ú Í≥ÑÏÇ∞

      return {
        calories: total.calories + (product.nutrition.calories * ratio),
        carb: total.carb + (product.nutrition.carb * ratio),
        protein: total.protein + (product.nutrition.protein * ratio),
        fat: total.fat + (product.nutrition.fat * ratio),
        sodium: total.sodium + (product.nutrition.sodium * ratio),
        sugar: total.sugar + (product.nutrition.sugar * ratio),
      };
    }, {
      calories: 0,
      carb: 0,
      protein: 0,
      fat: 0,
      sodium: 0,
      sugar: 0,
    });

    return (
      <Card 
        borderRadius={{ base: "lg", md: "xl" }}
        overflow="hidden" 
        shadow={{ base: "sm", md: "md" }}
        _hover={{ transform: { base: "none", md: "translateY(-2px)" }, shadow: { base: "md", md: "lg" } }}
        transition="all 0.2s ease"
        bg="white"
        border="1px"
        borderColor="gray.200"
      >
        <CardBody p={{ base: 3, md: 4 }}>
          <VStack spacing={{ base: 3, md: 4 }} align="stretch" display={{ base: "flex", md: "none" }}>
            {/* Î™®Î∞îÏùº Î†àÏù¥ÏïÑÏõÉ */}
            <HStack spacing={3} align="start">
              <Box position="relative" flexShrink={0}>
                <Image 
                  src={recipe.image} 
                  alt={recipe.name} 
                  w={{ base: "80px", sm: "100px" }}
                  h={{ base: "80px", sm: "100px" }}
                  objectFit="cover" 
                  borderRadius="md"
                  fallbackSrc="https://via.placeholder.com/100x100?text=üçΩÔ∏è"
                />
                <Badge 
                  position="absolute" 
                  top={1} 
                  right={1} 
                  colorScheme={recipe.difficulty === 'easy' ? 'green' : recipe.difficulty === 'medium' ? 'yellow' : 'red'}
                  fontSize="xs"
                  borderRadius="md"
                >
                  {recipe.difficulty === 'easy' ? 'Ïâ¨ÏõÄ' : recipe.difficulty === 'medium' ? 'Î≥¥ÌÜµ' : 'Ïñ¥Î†§ÏõÄ'}
                </Badge>
              </Box>

              <VStack align="start" spacing={2} flex={1} minW={0}>
                <Heading size="sm" color="gray.800" noOfLines={2} lineHeight="shorter">
                  {recipe.name}
                </Heading>
                <Text color="gray.600" fontSize="xs" noOfLines={2} lineHeight="1.3">
                  {recipe.description}
                </Text>

                <HStack spacing={3} wrap="wrap" w="full">
                  <HStack spacing={1}>
                    <TimeIcon boxSize={3} color="orange.500" />
                    <Text fontSize="xs" fontWeight="medium">{recipe.cookingTime}Î∂Ñ</Text>
                  </HStack>
                  <Text fontSize="xs" color="gray.500">Ïû¨Î£å {ingredients.length}Í∞ú</Text>
                  <Text fontSize="sm" fontWeight="bold" color="teal.600">
                    {totalPrice.toLocaleString()}Ïõê
                  </Text>
                </HStack>
              </VStack>
            </HStack>

            {/* Î™®Î∞îÏùº ÏòÅÏñëÏ†ïÎ≥¥ */}
            <HStack spacing={2} justify="space-between" align="center" w="full" bg="gray.50" p={2} borderRadius="md">
              <VStack spacing={0} align="center" minW="45px">
                <Text fontSize="xs" color="orange.600" fontWeight="bold">
                  {Math.round(totalNutrition.calories)}
                </Text>
                <Text fontSize="xs" color="gray.500">kcal</Text>
              </VStack>
              <VStack spacing={0} align="center" minW="35px">
                <Text fontSize="xs" color="blue.600" fontWeight="bold">
                  {Math.round(totalNutrition.carb)}g
                </Text>
                <Text fontSize="xs" color="gray.500">ÌÉÑÏàò</Text>
              </VStack>
              <VStack spacing={0} align="center" minW="35px">
                <Text fontSize="xs" color="red.600" fontWeight="bold">
                  {Math.round(totalNutrition.protein)}g
                </Text>
                <Text fontSize="xs" color="gray.500">Îã®Î∞±Ïßà</Text>
              </VStack>
              <VStack spacing={0} align="center" minW="35px">
                <Text fontSize="xs" color="green.600" fontWeight="bold">
                  {Math.round(totalNutrition.fat)}g
                </Text>
                <Text fontSize="xs" color="gray.500">ÏßÄÎ∞©</Text>
              </VStack>
            </HStack>
          </VStack>

          {/* Îç∞Ïä§ÌÅ¨ÌÜ± Î†àÏù¥ÏïÑÏõÉ */}
          <HStack spacing={4} align="start" display={{ base: "none", md: "flex" }}>
            {/* üì∏ Ïª¥Ìå©Ìä∏ Ïù¥ÎØ∏ÏßÄ */}
            <Box position="relative" flexShrink={0}>
              <Image 
                src={recipe.image} 
                alt={recipe.name} 
                w="120px" 
                h="120px" 
                objectFit="cover" 
                borderRadius="lg"
                fallbackSrc="https://via.placeholder.com/120x120?text=üçΩÔ∏è"
              />
              <Badge 
                position="absolute" 
                top={2} 
                right={2} 
                colorScheme={recipe.difficulty === 'easy' ? 'green' : recipe.difficulty === 'medium' ? 'orange' : 'red'} 
                size="sm" 
                borderRadius="md"
              >
                {recipe.difficulty === 'easy' ? 'Ïâ¨ÏõÄ' : recipe.difficulty === 'medium' ? 'Î≥¥ÌÜµ' : 'Ïñ¥Î†§ÏõÄ'}
              </Badge>
            </Box>

            {/* üìù Î†àÏãúÌîº Ï†ïÎ≥¥ */}
            <VStack spacing={3} align="stretch" flex={1}>
              <VStack spacing={2} align="stretch">
                <Heading size="md" color="gray.800" fontWeight="600" noOfLines={1}>
                  {recipe.name}
                </Heading>
                <Text color="gray.600" fontSize="sm" noOfLines={2} lineHeight="1.4">
                  {recipe.description}
                </Text>

                {/* ÌïµÏã¨ Ï†ïÎ≥¥ Ïª¥Ìå©Ìä∏ ÌëúÏãú */}
                <HStack spacing={4} wrap="wrap">
                  <HStack spacing={1}>
                    <TimeIcon boxSize={3} color="orange.500" />
                    <Text fontSize="xs" fontWeight="medium">{recipe.cookingTime}Î∂Ñ</Text>
                  </HStack>
                  <Text fontSize="xs" color="gray.500">Ïû¨Î£å {ingredients.length}Í∞ú</Text>
                  <Text fontSize="sm" fontWeight="bold" color="teal.600">
                    {totalPrice.toLocaleString()}Ïõê
                  </Text>
                </HStack>

                {/* ÏòÅÏñëÏ†ïÎ≥¥ */}
                <HStack spacing={3} justify="space-between" align="center" w="full" bg="gray.50" p={2} borderRadius="md">
                  <VStack spacing={0} align="center" minW="50px">
                    <Text fontSize="xs" color="orange.600" fontWeight="bold">
                            {Math.round(totalNutrition.calories)}
                          </Text>
                    <Text fontSize="xs" color="gray.500">kcal</Text>
                        </VStack>
                  <VStack spacing={0} align="center" minW="40px">
