  // üî• Í≥†ÏÜç Ï†ÑÏ≤òÎ¶¨ (Î∞∞Ïπò Ï≤òÎ¶¨ + Î©îÎ™®Ïù¥Ï†úÏù¥ÏÖò)
  private async fastPreprocessRecipes(recipes: Recipe[]): Promise<Array<{
    recipe: Recipe;
    nutrition: { calories: number; protein: number; carbs: number; fat: number };
    monthlyCost: number;
    score: number;
  }>> {
    const processed = [];

    for (const recipe of recipes) {
      // Ï∫êÏãúÎêú ÏòÅÏñëÏÜå Ï†ïÎ≥¥ ÌôïÏù∏
      let nutrition = this.nutritionCache[recipe.id];
      if (!nutrition) {
        // Í∞ÑÎã®Ìïú Ï∂îÏ†ïÏπò ÏÇ¨Ïö© (Ïã§Ï†ú Í≥ÑÏÇ∞ ÎåÄÏã†)
        nutrition = this.estimateNutrition(recipe);
        this.nutritionCache[recipe.id] = nutrition;
      }

      processed.push({
        recipe,
        nutrition,
        monthlyCost: nutrition.monthlyCost,
        score: this.calculateSimpleScore(recipe, nutrition)
      });
    }

    return processed;
  }

  // ‚ö° Í∞ÑÎã®Ìïú ÏòÅÏñëÏÜå Ï∂îÏ†ï (Î≥µÏû°Ìïú Í≥ÑÏÇ∞ ÎåÄÏã†)
  private estimateNutrition(recipe: Recipe): {
    calories: number; protein: number; carbs: number; fat: number; monthlyCost: number;
  } {
    // Î†àÏãúÌîº Ïù¥Î¶ÑÍ≥º ÌÉúÍ∑∏ Í∏∞Î∞ò Îπ†Î•∏ Ï∂îÏ†ï
    const name = recipe.name.toLowerCase();
    const tags = recipe.tags?.join(' ').toLowerCase() || '';

    let calories = 400; // Í∏∞Î≥∏Í∞í
    let protein = 20;
    let carbs = 50;
    let fat = 15;
    let monthlyCost = 45000; // Í∏∞Î≥∏ Ïõî ÎπÑÏö©

    // ÌÇ§ÏõåÎìú Í∏∞Î∞ò Îπ†Î•∏ Ï°∞Ï†ï
    if (name.includes('Îã≠') || name.includes('Í≥ÑÎûÄ') || tags.includes('Í≥†Îã®Î∞±')) {
      protein += 15;
      calories += 50;
      monthlyCost += 15000;
    }

    if (name.includes('ÏÉêÎü¨Îìú') || name.includes('ÏïºÏ±Ñ') || tags.includes('Ï†ÄÏπºÎ°úÎ¶¨')) {
      calories -= 150;
      carbs -= 20;
      monthlyCost -= 10000;
    }

    if (name.includes('Î∞•') || name.includes('Î©¥') || name.includes('ÌååÏä§ÌÉÄ')) {
      carbs += 30;
      calories += 100;
    }

    if (name.includes('Îì±Ïã¨') || name.includes('ÏÜåÍ≥†Í∏∞')) {
      protein += 20;
      fat += 10;
      calories += 100;
      monthlyCost += 25000;
    }

    return { calories, protein, carbs, fat, monthlyCost };
  }

  // ‚ö° Í∞ÑÎã®Ìïú Ï†êÏàò Í≥ÑÏÇ∞
  private calculateSimpleScore(recipe: Recipe, nutrition: any): number {
    const viewScore = Math.min((recipe.userRatings?.overall || 0) * 20, 100);
    const nutritionScore = Math.min(nutrition.protein * 2 + nutrition.calories * 0.1, 100);
    const costScore = Math.max(100 - (nutrition.monthlyCost / 1000), 0);

    return (viewScore * 0.4 + nutritionScore * 0.4 + costScore * 0.2);
  }

  // ‚ö° ÏòàÏÇ∞ Í∏∞Î∞ò Îπ†Î•∏ ÌïÑÌÑ∞ÎßÅ
  private fastBudgetFilter(
    recipes: Array<{ recipe: Recipe; nutrition: any; monthlyCost: number; score: number }>,
    monthlyBudget: number
  ) {
    const maxCostPerRecipe = monthlyBudget * 0.6; // Ìïú Î†àÏãúÌîºÍ∞Ä ÏòàÏÇ∞Ïùò 60% Ïù¥Ìïò

    return recipes
      .filter(item => item.monthlyCost <= maxCostPerRecipe)
      .sort((a, b) => b.score - a.score) // Ï†êÏàòÏàú Ï†ïÎ†¨
      .slice(0, 20); // ÏÉÅÏúÑ 20Í∞úÎßå Ï≤òÎ¶¨
  }

