    if (affordableRecipes.length === 0) {
      console.log('‚ö†Ô∏è ÏÇ¨Ïö© Í∞ÄÎä•Ìïú Î†àÏãúÌîºÍ∞Ä ÏóÜÏùå');
      return [];
    }

    // üî• Ìú¥Î¶¨Ïä§Ìã±: Í∞ÄÍ≤©-ÏÑ±Îä•ÎπÑ Í∏∞Î∞ò Í∑∏Î¶¨Îîî ÏïåÍ≥†Î¶¨Ï¶ò
    let remainingBudget = monthlyBudget;
    const selectedRecipes: Recipe[] = [];

    // Í∞ÄÍ≤© ÎåÄÎπÑ Ï†êÏàò Í∏∞Ï§ÄÏúºÎ°ú Ïû¨Ï†ïÎ†¨
    const costEffectiveRecipes = affordableRecipes
      .map(item => ({
        ...item,
        efficiency: item.monthlyCost > 0 ? (item.score / (item.monthlyCost / 10000)) : item.score // 0ÏúºÎ°ú ÎÇòÎàÑÍ∏∞ Î∞©ÏßÄ
      }))
      .sort((a, b) => b.efficiency - a.efficiency);

    console.log('üí∞ Í∞ÄÍ≤©-ÏÑ±Îä•ÎπÑ ÏÉÅÏúÑ 5Í∞ú:', costEffectiveRecipes.slice(0, 5).map(item => ({
      name: item.recipe.name,
      cost: item.monthlyCost,
      score: item.score,
      efficiency: item.efficiency
    })));

    // Í∑∏Î¶¨Îîî ÏÑ†ÌÉù (ÏµúÎåÄ 3Í∞ú)
    for (const item of costEffectiveRecipes) {
      if (selectedRecipes.length >= 3) break;

      const canAfford = item.monthlyCost <= remainingBudget * 0.9; // 90% ÏòàÏÇ∞ ÏÇ¨Ïö©
      console.log(`üîç Í≤ÄÌÜ†: ${item.recipe.name} (ÎπÑÏö©: ${item.monthlyCost}, ÏòàÏÇ∞Ïó¨Ïú†: ${remainingBudget}, Í∞ÄÎä•: ${canAfford})`);

      if (canAfford) {
        selectedRecipes.push(item.recipe);
        remainingBudget -= item.monthlyCost;
        console.log(`‚úÖ ÏÑ†ÌÉù: ${item.recipe.name}`);
      }
    }

    // ÏòàÏÇ∞ Ï†úÌïúÏù¥ ÎÑàÎ¨¥ ÏóÑÍ≤©ÌïòÎ©¥ ÏôÑÌôî
    if (selectedRecipes.length === 0) {
      console.log('‚ö†Ô∏è ÏòàÏÇ∞ Ï°∞Í±¥ ÏôÑÌôîÌï¥ÏÑú Ïû¨ÏãúÎèÑ');
      for (const item of costEffectiveRecipes.slice(0, 3)) {
        selectedRecipes.push(item.recipe);
        if (selectedRecipes.length >= 3) break;
      }
    }

    // ÏµúÏÜå 1Í∞úÎäî ÏÑ†ÌÉù Î≥¥Ïû•
    if (selectedRecipes.length === 0 && affordableRecipes.length > 0) {
      selectedRecipes.push(affordableRecipes[0].recipe);
      console.log('üõ°Ô∏è ÏµúÏÜå 1Í∞ú Î≥¥Ïû•:', affordableRecipes[0].recipe.name);
    }

    console.log(`‚ö° Í≥†ÏÜç Ï°∞Ìï© ÏôÑÎ£å: ${selectedRecipes.length}Í∞ú Î†àÏãúÌîº ÏÑ†ÌÉù`);
    return selectedRecipes;
  }

  // üìä ÏòàÏÇ∞ Î∂ÑÏÑù ÏÉùÏÑ±
  private generateBudgetAnalysis(recipes: Recipe[], monthlyBudget: number) {
    const costBreakdown = recipes.map(recipe => {
      const nutrition = this.nutritionCache[recipe.id];
      const monthlyCost = nutrition?.monthlyCost || 45000;

      return {
        recipeId: recipe.id,
        recipeName: recipe.name,
        monthlyCost,
        costPercentage: (monthlyCost / monthlyBudget) * 100
      };
    });

    const totalEstimatedCost = costBreakdown.reduce((sum, item) => sum + item.monthlyCost, 0);
    const budgetUsagePercentage = (totalEstimatedCost / monthlyBudget) * 100;

    return {
      totalEstimatedCost,
      budgetUsagePercentage,
      costBreakdown
    };
  }

