import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  VStack,
  HStack,
  Text,
  Button,
  Card,
  CardBody,
  Heading,
  Image,
  Badge,
  Grid,
  GridItem,
  Progress,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatArrow,
  useToast,
  Icon,
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  OrderedList,
  ListItem,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  Circle,
} from '@chakra-ui/react';
import { ArrowBackIcon, ExternalLinkIcon, TimeIcon, InfoIcon } from '@chakra-ui/icons';
import { useAppContext } from '../context/AppContext';
import { CoupangProduct, Recipe } from '../types';
import { getRecipesByGoal, getRecipeIngredients, recipeLoader } from '../data/recipeData';
import { calculateRecipeFrequency } from '../utils/personalizedRecommendation';
import { debugRecipeMapping } from '../utils/recipeDebugger';

import Layout from './Layout/Layout';

const RecommendationScreen: React.FC = () => {
  const navigate = useNavigate();
  const toast = useToast();
  const {
    userInput,
    recommendationResult,
    setSelectedProduct,
    userProfile // 사용자 목표 정보 가져오기
  } = useAppContext();

  // 🍳 사용자 목표에 맞는 레시피 가져오기 (비동기) - hooks를 조건부 리턴 전에 선언
  const [recommendedRecipes, setRecommendedRecipes] = useState<Recipe[]>([]);
  const [isLoadingRecipes, setIsLoadingRecipes] = useState(true);
  const [recipeStats, setRecipeStats] = useState<any>(null);

  useEffect(() => {
    // 추천 결과가 없으면 메인 페이지로 리다이렉트
    if (!recommendationResult) {
      toast({
        title: "추천 결과를 찾을 수 없습니다",
        description: "메인 페이지로 돌아가서 다시 시도해주세요.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      navigate('/');
      return;
    }

    const loadRecipes = async () => {
      setIsLoadingRecipes(true);
      try {
        // 🔍 디버깅 실행
        console.log('🔍 === 레시피 매핑 디버깅 시작 ===');
        debugRecipeMapping();

        // 캐시 진단
        console.log('🔍 === 캐시 진단 시작 ===');
        await recipeLoader.diagnoseCacheStatus();

        // 사용자 목표 확인 및 설정
        const userGoal = userProfile.goal || 'maintenance';
        console.log('🎯 사용자 목표:', userGoal);

        // 목표가 없으면 기본값으로 설정
        if (!userProfile.goal) {
          console.log('⚠️ 사용자 목표가 설정되지 않음, 기본값(maintenance)으로 설정');
        }

        // 레시피 로드
        const recipes = await getRecipesByGoal(userGoal);
        console.log('📋 로드된 레시피 수:', recipes.length);
        console.log('📋 레시피 ID들:', recipes.map(r => r.id));

        if (recipes.length === 0) {
          console.error('⚠️ 레시피가 하나도 로드되지 않았습니다!');
          console.log('💡 CSV 파일이 제대로 로드되는지 네트워크 탭에서 확인하세요');
          console.log('💡 브라우저 콘솔에서 에러 메시지를 확인하세요');

          // 통계 정보로 CSV 로드 여부 확인
          const stats = await recipeLoader.getStatistics();
          console.log('📊 CSV 통계:', stats);
          if (stats.totalRecipes === 0) {
            console.error('❌ CSV 파일이 로드되지 않았습니다!');
          }
        }

        setRecommendedRecipes(recipes);

        // 통계 정보 로드
        const stats = await recipeLoader.getStatistics();
        setRecipeStats(stats);
      } catch (error) {
        console.error('❌ 레시피 로드 실패:', error);

        // 에러 상세 정보
        if (error instanceof Error) {
          console.error('에러 메시지:', error.message);
          console.error('에러 스택:', error.stack);
        }

        // 빈 배열로 설정하여 UI 크래시 방지
        setRecommendedRecipes([]);
      } finally {
        setIsLoadingRecipes(false);
      }
    };

    loadRecipes();
  }, [userProfile.goal]);

  // recommendationResult가 없으면 로딩 상태 표시
  if (!recommendationResult) {
    return (
      <Layout>
        <Container maxW="container.xl" py={8}>
          <Text textAlign="center">로딩 중...</Text>
        </Container>
      </Layout>
    );
  }

  const { 
    totalBudgetUsed, 
    budgetRemaining, 
    nutritionBalance, 
    message, 
    nutritionTargets,
    userInsights 
  } = recommendationResult;

  // 시간대별 레시피 분류
  const mealTypeRecipes = {
    breakfast: recommendedRecipes.filter(r => r.mealType === 'breakfast'),
    lunch: recommendedRecipes.filter(r => r.mealType === 'lunch'),
    dinner: recommendedRecipes.filter(r => r.mealType === 'dinner'),
  };

  // 상품 클릭 핸들러
  const handleProductClick = (product: CoupangProduct) => {
    setSelectedProduct(product);
    navigate(`/product/${product.id}`);
  };

  // 다시 입력하기 핸들러
  const handleBackToInput = () => {
    navigate('/');
  };

  const budgetUsagePercentage = (totalBudgetUsed / userInput.budget) * 100;

  // 🎨 컴팩트 레시피 카드 컴포넌트
  const CompactRecipeCard: React.FC<{ recipe: Recipe }> = ({ recipe }) => {
    const [showAllIngredients, setShowAllIngredients] = useState(false);
    const [showAllInstructions, setShowAllInstructions] = useState(false);
    const ingredients = getRecipeIngredients(recipe.id);
    const totalPrice = ingredients.reduce((sum: number, ing: any) => sum + ing.product.price, 0);

    // 🍽️ 레시피 제조 빈도 분석
    const recipeFrequency = calculateRecipeFrequency(recipe.id);

    // 🧮 영양소 계산 (재료별 영양소 × 사용량)
    const totalNutrition = ingredients.reduce((total: any, ing: any) => {
      const product = ing.product;
      const ratio = ing.quantity / 100; // 100g 기준으로 계산

      return {
        calories: total.calories + (product.nutrition.calories * ratio),
        carb: total.carb + (product.nutrition.carb * ratio),
        protein: total.protein + (product.nutrition.protein * ratio),
        fat: total.fat + (product.nutrition.fat * ratio),
        sodium: total.sodium + (product.nutrition.sodium * ratio),
        sugar: total.sugar + (product.nutrition.sugar * ratio),
      };
    }, {
      calories: 0,
      carb: 0,
      protein: 0,
      fat: 0,
      sodium: 0,
      sugar: 0,
    });

    return (
      <Card 
        borderRadius="xl" 
        overflow="hidden" 
        shadow="md"
        _hover={{ transform: "translateY(-2px)", shadow: "lg" }}
        transition="all 0.2s ease"
        bg="white"
        border="1px"
        borderColor="gray.200"
      >
        <CardBody p={4}>
          <HStack spacing={4} align="start">
            {/* 📸 컴팩트 이미지 */}
            <Box position="relative" flexShrink={0}>
              <Image 
                src={recipe.image} 
                alt={recipe.name} 
                w="120px" 
                h="120px" 
                objectFit="cover" 
                borderRadius="lg"
                fallbackSrc="https://via.placeholder.com/120x120?text=🍽️"
              />
              <Badge 
                position="absolute" 
                top={2} 
                right={2} 
                colorScheme={recipe.difficulty === 'easy' ? 'green' : recipe.difficulty === 'medium' ? 'orange' : 'red'} 
                size="sm" 
                borderRadius="md"
              >
                {recipe.difficulty === 'easy' ? '쉬움' : recipe.difficulty === 'medium' ? '보통' : '어려움'}
              </Badge>
            </Box>

            {/* 📝 레시피 정보 */}
            <VStack spacing={3} align="stretch" flex={1}>
              <VStack spacing={2} align="stretch">
                <Heading size="md" color="gray.800" fontWeight="600" noOfLines={1}>
                  {recipe.name}
                </Heading>
                <Text color="gray.600" fontSize="sm" noOfLines={2} lineHeight="1.4">
                  {recipe.description}
                </Text>

                {/* 핵심 정보 컴팩트 표시 */}
                <HStack spacing={4} wrap="wrap">
                  <HStack spacing={1}>
                    <TimeIcon boxSize={3} color="orange.500" />
                    <Text fontSize="xs" fontWeight="medium">{recipe.cookingTime}분</Text>
                  </HStack>
                  <Text fontSize="xs" color="gray.500">재료 {ingredients.length}개</Text>
                  <Text fontSize="sm" fontWeight="bold" color="teal.600">
                    {totalPrice.toLocaleString()}원
                  </Text>
                  <Badge colorScheme="blue" size="sm" variant="subtle">
                    예산 {((totalPrice / userInput.budget) * 100).toFixed(0)}%
                  </Badge>
                </HStack>

                {/* 🍽️ 제조 가능 횟수 정보 - 대시보드 연계 디자인 */}
                <Card bg="linear-gradient(135deg, #4299e1 0%, #3182ce 100%)" borderRadius="lg" overflow="hidden">
                  <CardBody p={3}>
                    <HStack spacing={3} justify="space-between" align="center">
                      <VStack spacing={0} align="start">
                        <HStack spacing={2}>
                          <Text fontSize="lg">📦</Text>
                          <Text fontSize="xs" color="white" fontWeight="medium">
                            구매 후 제조 가능
                          </Text>
                        </HStack>
                        <HStack spacing={1}>
                          <Text fontSize="xl" fontWeight="bold" color="white">
                            {recipeFrequency.minPossibleRecipes}
                          </Text>
                          <Text fontSize="sm" color="whiteAlpha.800">번</Text>
                          <Text fontSize="xs" color="whiteAlpha.600">
                            (최소 기준)
                          </Text>
                        </HStack>
                      </VStack>
                      <VStack spacing={0} align="end">
                        <Text fontSize="xs" color="whiteAlpha.700">
                          1회당 비용
                        </Text>
                        <Text fontSize="lg" fontWeight="bold" color="yellow.200">
                          {recipeFrequency.totalCostPerRecipe.toLocaleString()}원
                        </Text>
                        <Text fontSize="xs" color="whiteAlpha.600">
                          끼니 예산: {userInsights?.budgetPerMeal.toLocaleString()}원
                        </Text>
                      </VStack>
                    </HStack>
                  </CardBody>
                </Card>

                {/* 🔥 영양정보 섹션 - 대시보드 연계 디자인 */}
                <Card bg="linear-gradient(135deg, #38b2ac 0%, #319795 100%)" borderRadius="lg" overflow="hidden">
                  <CardBody p={3}>
                    <VStack spacing={3}>
                      {/* 헤더 */}
                      <HStack spacing={2} w="full" justify="center">
                        <Text fontSize="lg">🍎</Text>
                        <Text fontSize="sm" color="white" fontWeight="bold">
                          1회분 영양정보
                        </Text>
                        <Text fontSize="xs" color="whiteAlpha.700">
                          (일일목표 대비)
                        </Text>
                      </HStack>

                      {/* 총 영양정보 */}
                      <HStack justify="space-between" align="center" wrap="wrap" w="full">
                        <VStack spacing={1} align="center" bg="whiteAlpha.200" p={2} borderRadius="md" minW="65px">
                          <Text fontSize="xs" color="whiteAlpha.800">총 칼로리</Text>
                          <Text fontSize="lg" fontWeight="bold" color="white">
                            {Math.round(totalNutrition.calories)}
                          </Text>
                          <Text fontSize="xs" color="whiteAlpha.700">kcal</Text>
                          {userInsights && (
                            <Text fontSize="xs" color="yellow.200">
                              {((totalNutrition.calories / userInsights.dailyCalorieGoal) * 100).toFixed(0)}%
                            </Text>
                          )}
                        </VStack>

                        <VStack spacing={1} align="center" bg="whiteAlpha.200" p={2} borderRadius="md" minW="50px">
                          <Text fontSize="xs" color="whiteAlpha.800">탄수화물</Text>
                          <Text fontSize="sm" fontWeight="bold" color="white">
                            {Math.round(totalNutrition.carb)}g
                          </Text>
                          <Text fontSize="xs" color="orange.200">
                            {totalNutrition.calories > 0 ? Math.round((totalNutrition.carb * 4 / totalNutrition.calories) * 100) : 0}%
                          </Text>
                        </VStack>

                        <VStack spacing={1} align="center" bg="whiteAlpha.200" p={2} borderRadius="md" minW="50px">
                          <Text fontSize="xs" color="whiteAlpha.800">단백질</Text>
                          <Text fontSize="sm" fontWeight="bold" color="white">
                            {Math.round(totalNutrition.protein)}g
                          </Text>
                          <Text fontSize="xs" color="red.200">
                            {totalNutrition.calories > 0 ? Math.round((totalNutrition.protein * 4 / totalNutrition.calories) * 100) : 0}%
                          </Text>
                        </VStack>

                        <VStack spacing={1} align="center" bg="whiteAlpha.200" p={2} borderRadius="md" minW="50px">
                          <Text fontSize="xs" color="whiteAlpha.800">지방</Text>
                          <Text fontSize="sm" fontWeight="bold" color="white">
                            {Math.round(totalNutrition.fat)}g
                          </Text>
                          <Text fontSize="xs" color="green.200">
                            {totalNutrition.calories > 0 ? Math.round((totalNutrition.fat * 9 / totalNutrition.calories) * 100) : 0}%
                          </Text>
                        </VStack>
                      </HStack>

                      {/* 📊 재료별 칼로리 기여도 시각화 */}
                      <Box w="full" bg="whiteAlpha.200" p={3} borderRadius="md">
                        <HStack spacing={2} mb={2}>
                          <Text fontSize="lg">📊</Text>
                          <Text fontSize="xs" color="white" fontWeight="bold">재료별 칼로리 기여도</Text>
                        </HStack>
                        <VStack spacing={1} align="stretch">
                          {ingredients.slice(0, 4).map((ingredient: any, index: number) => {
                            const ingredientCalories = Math.round(ingredient.product.nutrition.calories * ingredient.quantity / 100);
                            const contribution = totalNutrition.calories > 0 ? (ingredientCalories / totalNutrition.calories) * 100 : 0;
                            return (
                              <HStack key={index} spacing={2} align="center">
                                <Text fontSize="xs" color="whiteAlpha.900" minW="70px" noOfLines={1} fontWeight="medium">
                                  {ingredient.product.name.split(' ')[0]} {ingredient.quantity}{ingredient.unit}
                                </Text>
                                <Box flex={1} bg="whiteAlpha.400" borderRadius="full" h="6px" position="relative">
                                  <Box 
                                    bg={index === 0 ? "yellow.300" : index === 1 ? "orange.300" : index === 2 ? "red.300" : "green.300"}
                                    h="full" 
                                    borderRadius="full" 
                                    w={`${contribution}%`}
                                    boxShadow="inset 0 1px 0 rgba(255,255,255,0.3)"
                                  />
                                </Box>
                                <Text fontSize="xs" color="white" minW="35px" fontWeight="bold">
                                  {ingredientCalories}kcal
                                </Text>
                                <Text fontSize="xs" color="yellow.200" minW="30px" fontWeight="bold">
                                  {Math.round(contribution)}%
                                </Text>
                              </HStack>
                            );
                          })}
                        </VStack>
                      </Box>
                    </VStack>
                  </CardBody>
                </Card>

                {/* 태그는 최대 3개만 표시 */}
                <HStack spacing={1} wrap="wrap">
                  {recipe.tags.slice(0, 3).map((tag, index) => (
                    <Badge key={index} size="sm" colorScheme="teal" variant="outline" borderRadius="md" fontSize="xs">
                      #{tag}
                    </Badge>
                  ))}
                  {recipe.tags.length > 3 && (
                    <Text fontSize="xs" color="gray.400">+{recipe.tags.length - 3}</Text>
                  )}
                </HStack>
              </VStack>
            </VStack>

            {/* 🎯 레시피 상세 정보 */}
            <VStack spacing={2} flexShrink={0}>
              <Accordion allowToggle w="full">
                <AccordionItem border="none">
                  <AccordionButton 
                    borderRadius="lg"
                    bg="linear-gradient(135deg, #e2e8f0 0%, #cbd5e0 100%)"
                    border="1px solid"
                    borderColor="gray.300"
                    px={3}
                    py={2}
                    fontSize="xs"
                    fontWeight="bold"
                    color="gray.700"
                    _hover={{ bg: "linear-gradient(135deg, #cbd5e0 0%, #a0aec0 100%)", transform: "translateY(-1px)" }}
                    _expanded={{ bg: "linear-gradient(135deg, #a0aec0 0%, #718096 100%)", color: "white" }}
                    justifyContent="center"
                    transition="all 0.2s ease"
                  >
                    <Text fontSize="xs" mr={2}>상세보기</Text>
                    <AccordionIcon />
                  </AccordionButton>
                  <AccordionPanel px={0} py={3}>
                    <VStack spacing={3} align="stretch">
                      {/* 간단한 재료 목록 */}
                      <Box>
                        <HStack justify="space-between" align="center" mb={3}>
                          <Text fontSize="sm" fontWeight="medium" color="gray.700">
                            🛒 필요한 재료
                          </Text>
                          <Card bg="gray.50" size="sm" borderRadius="md">
                            <CardBody p={2}>
                              <HStack spacing={2}>
                                <Text fontSize="xs" color="orange.600" fontWeight="bold">
                                  총 {Math.round(totalNutrition.calories)}kcal
                                </Text>
                                <Text fontSize="xs" color="gray.500">|</Text>
                                <Text fontSize="xs" color="blue.600">
                                  탄{Math.round(totalNutrition.carb)}g
                                </Text>
                                <Text fontSize="xs" color="red.600">
                                  단{Math.round(totalNutrition.protein)}g
                                </Text>
                                <Text fontSize="xs" color="green.600">
                                  지{Math.round(totalNutrition.fat)}g
                                </Text>
                              </HStack>
                            </CardBody>
                          </Card>
                        </HStack>
                        <VStack spacing={2} align="stretch">
                          {(showAllIngredients ? ingredients : ingredients.slice(0, 3)).map((ingredient: any, index: number) => (
                            <HStack 
                              key={index} 
                              spacing={3} 
                              p={2} 
                              bg="gray.50" 
                              borderRadius="md" 
                              cursor="pointer" 
                              _hover={{ bg: "gray.100" }} 
                              onClick={() => handleProductClick(ingredient.product)}
                            >
                              <Image 
                                src={ingredient.product.imageUrl} 
                                alt={ingredient.product.name} 
                                boxSize="40px" 
                                objectFit="cover" 
                                borderRadius="md" 
                                fallbackSrc="https://via.placeholder.com/40x40?text=🛒" 
                              />
                              <VStack align="start" spacing={0} flex={1}>
                                <Text fontSize="xs" fontWeight="medium" noOfLines={1}>
                                  {ingredient.product.name}
                                </Text>
                                <HStack spacing={2}>
                                  <Text fontSize="xs" color="gray.600">
                                    {ingredient.quantity}{ingredient.unit}
                                  </Text>
                                  <Text fontSize="xs" fontWeight="bold" color="red.500">
                                    {ingredient.product.price.toLocaleString()}원
                                  </Text>
                                </HStack>
                                {/* 🧮 재료별 상세 영양정보 및 기여도 */}
                                <VStack spacing={1} mt={2} align="stretch">
                                  <HStack spacing={2} justify="space-between">
                                    <Text fontSize="xs" color="gray.600" fontWeight="medium">
                                      📊 {ingredient.quantity}{ingredient.unit} 영양정보
                                    </Text>
                                    <Text fontSize="xs" color="gray.500">
                                      ({Math.round((ingredient.product.nutrition.calories * ingredient.quantity / 100 / totalNutrition.calories) * 100)}% 기여)
                                    </Text>
                                  </HStack>
                                  <HStack spacing={3} wrap="wrap">
                                    <VStack spacing={0} align="center" minW="45px">
                                      <Text fontSize="xs" color="orange.600" fontWeight="bold">
                                        {Math.round(ingredient.product.nutrition.calories * ingredient.quantity / 100)}
                                      </Text>
                                      <Text fontSize="xs" color="gray.500">kcal</Text>
                                    </VStack>
                                    <VStack spacing={0} align="center" minW="35px">
                                      <Text fontSize="xs" color="blue.600" fontWeight="bold">
                                        {Math.round(ingredient.product.nutrition.carb * ingredient.quantity / 100)}g
                                      </Text>
                                      <Text fontSize="xs" color="gray.500">탄</Text>
                                    </VStack>
                                    <VStack spacing={0} align="center" minW="35px">
                                      <Text fontSize="xs" color="red.600" fontWeight="bold">
                                        {Math.round(ingredient.product.nutrition.protein * ingredient.quantity / 100)}g
                                      </Text>
                                      <Text fontSize="xs" color="gray.500">단</Text>
                                    </VStack>
                                    <VStack spacing={0} align="center" minW="35px">
                                      <Text fontSize="xs" color="green.600" fontWeight="bold">
                                        {Math.round(ingredient.product.nutrition.fat * ingredient.quantity / 100)}g
                                      </Text>
                                      <Text fontSize="xs" color="gray.500">지</Text>
                                    </VStack>
                                  </HStack>

                                  {/* 🍽️ 재료별 제조 가능 횟수 */}
                                  <Box mt={2} p={2} bg="teal.50" borderRadius="md" borderWidth={1} borderColor="teal.200">
                                    {(() => {
                                      const ingredientAnalysis = recipeFrequency.ingredientAnalysis.find(
                                        analysis => analysis.productName === ingredient.product.name
                                      );
                                      return ingredientAnalysis ? (
                                        <VStack spacing={1} align="stretch">
                                          <HStack justify="space-between" align="center">
                                            <Text fontSize="xs" color="teal.700" fontWeight="medium">
                                              📦 구매 시
                                            </Text>
                                            <Text fontSize="xs" color="gray.600">
                                              {ingredientAnalysis.productPackageSize}
                                            </Text>
                                          </HStack>
                                          <HStack justify="space-between" align="center">
                                            <Text fontSize="xs" color="gray.600">
                                              이 재료로 가능:
                                            </Text>
                                            <HStack spacing={1}>
                                              <Text fontSize="sm" fontWeight="bold" color="teal.600">
                                                {ingredientAnalysis.maxRecipesFromThisIngredient}
                                              </Text>
                                              <Text fontSize="xs" color="gray.600">번</Text>
                                            </HStack>
                                          </HStack>
                                          <HStack justify="space-between" align="center">
                                            <Text fontSize="xs" color="gray.600">
                                              1회당 재료비:
                                            </Text>
                                            <Text fontSize="xs" fontWeight="bold" color="red.600">
                                              {ingredientAnalysis.costPerRecipe.toLocaleString()}원
                                            </Text>
                                          </HStack>
                                        </VStack>
                                      ) : (
                                        <Text fontSize="xs" color="gray.500">분석 정보 없음</Text>
                                      );
                                    })()}
                                  </Box>
                                </VStack>
                              </VStack>
                              <Icon as={ExternalLinkIcon} boxSize={3} color="gray.400" />
                            </HStack>
                          ))}
                          {ingredients.length > 3 && (
                            <Button 
                              variant="ghost" 
                              size="sm" 
                              fontSize="xs" 
                              color="teal.600"
                              _hover={{ bg: "teal.50" }}
                              onClick={() => setShowAllIngredients(!showAllIngredients)}
                            >
                              {showAllIngredients 
                                ? "접기" 
                                : `+${ingredients.length - 3}개 재료 더보기`
                              }
                            </Button>
                          )}
                        </VStack>
                      </Box>

                      {/* 🍽️ 맛있는 레시피 정보 섹션 */}
                      <VStack spacing={4} align="stretch">
                        {/* 맛 프로필 */}
                        {recipe.flavorProfile && (
                          <Box p={3} bg="purple.50" borderRadius="md" border="1px" borderColor="purple.200">
                            <HStack spacing={2} mb={2}>
                              <Text fontSize="lg">👅</Text>
                              <Text fontSize="sm" fontWeight="bold" color="purple.700">맛 프로필</Text>
                            </HStack>
                            <VStack spacing={2} align="stretch">
                              <HStack spacing={2} wrap="wrap">
                                {recipe.flavorProfile.taste.map((taste, idx) => (
                                  <Badge key={idx} colorScheme="purple" variant="subtle" fontSize="xs">
                                    {taste}
                                  </Badge>
                                ))}
                                <Badge colorScheme="gray" variant="outline" fontSize="xs">
                                  {recipe.flavorProfile.intensity === 'light' ? '순한맛' : 
                                   recipe.flavorProfile.intensity === 'medium' ? '보통맛' : '진한맛'}
                                </Badge>
                              </HStack>
                              <Text fontSize="xs" color="purple.600" fontStyle="italic">
                                {recipe.flavorProfile.description}
                              </Text>
                            </VStack>
                          </Box>
                        )}

                        {/* 조리 팁 */}
                        {recipe.cookingTips && (
                          <Box p={3} bg="blue.50" borderRadius="md" border="1px" borderColor="blue.200">
                            <HStack spacing={2} mb={2}>
                              <Text fontSize="lg">💡</Text>
                              <Text fontSize="sm" fontWeight="bold" color="blue.700">조리 팁</Text>
                            </HStack>
                            <VStack spacing={2} align="stretch">
                              {recipe.cookingTips.cooking.slice(0, 2).map((tip, idx) => (
                                <HStack key={idx} spacing={2} align="start">
                                  <Text fontSize="xs" color="blue.600">•</Text>
                                  <Text fontSize="xs" color="blue.600">{tip}</Text>
                                </HStack>
                              ))}
                            </VStack>
                          </Box>
                        )}

                        {/* 영양 하이라이트 */}
                        {recipe.nutritionHighlights && (
                          <Box p={3} bg="green.50" borderRadius="md" border="1px" borderColor="green.200">
                            <HStack spacing={2} mb={2}>
                              <Text fontSize="lg">🌿</Text>
                              <Text fontSize="sm" fontWeight="bold" color="green.700">영양 포인트</Text>
                            </HStack>
                            <VStack spacing={1} align="stretch">
                              {recipe.nutritionHighlights.mainBenefits.slice(0, 2).map((benefit, idx) => (
                                <HStack key={idx} spacing={2} align="start">
                                  <Text fontSize="xs" color="green.600">✓</Text>
                                  <Text fontSize="xs" color="green.600">{benefit}</Text>
                                </HStack>
                              ))}
                              <Text fontSize="xs" color="green.500" fontWeight="medium" mt={1}>
                                {recipe.nutritionHighlights.calorieContext}
                              </Text>
                            </VStack>
                          </Box>
                        )}

                        {/* 셰프 팁 */}
                        {recipe.chefTips && recipe.chefTips.length > 0 && (
                          <Box p={3} bg="yellow.50" borderRadius="md" border="1px" borderColor="yellow.200">
                            <HStack spacing={2} mb={2}>
                              <Text fontSize="lg">👨‍🍳</Text>
                              <Text fontSize="sm" fontWeight="bold" color="yellow.700">전문가 팁</Text>
                            </HStack>
                            <Text fontSize="xs" color="yellow.600" fontWeight="medium">
                              {recipe.chefTips[0]}
                            </Text>
                          </Box>
                        )}

                        {/* 평점 정보 */}
                        {recipe.userRatings && (
                          <Box p={3} bg="orange.50" borderRadius="md" border="1px" borderColor="orange.200">
                            <HStack spacing={2} mb={2}>
                              <Text fontSize="lg">⭐</Text>
                              <Text fontSize="sm" fontWeight="bold" color="orange.700">사용자 평가</Text>
                            </HStack>
                            <HStack spacing={4}>
                              <VStack spacing={0} align="center">
                                <Text fontSize="lg" fontWeight="bold" color="orange.600">
                                  {recipe.userRatings.overall}
                                </Text>
                                <Text fontSize="xs" color="gray.600">전체</Text>
                              </VStack>
                              <VStack spacing={0} align="center">
                                <Text fontSize="sm" fontWeight="bold" color="red.500">
                                  {recipe.userRatings.taste}
                                </Text>
                                <Text fontSize="xs" color="gray.600">맛</Text>
                              </VStack>
                              <VStack spacing={0} align="center">
                                <Text fontSize="sm" fontWeight="bold" color="blue.500">
                                  {recipe.userRatings.difficulty}
                                </Text>
                                <Text fontSize="xs" color="gray.600">난이도</Text>
                              </VStack>
                              <VStack spacing={0} align="center">
                                <Text fontSize="sm" fontWeight="bold" color="green.500">
                                  {recipe.userRatings.nutrition}
                                </Text>
                                <Text fontSize="xs" color="gray.600">영양</Text>
                              </VStack>
                            </HStack>
                            <Text fontSize="xs" color="gray.500" mt={2}>
                              {recipe.userRatings.reviewCount.toLocaleString()}명이 평가했어요
                            </Text>
                          </Box>
                        )}

                        {/* 간단한 조리법 */}
                        <Box>
                          <Text fontSize="sm" fontWeight="medium" mb={2} color="gray.700">
                            👨‍🍳 조리 순서
                          </Text>
                          <VStack spacing={1} align="stretch">
                            {(showAllInstructions ? recipe.instructions : recipe.instructions.slice(0, 2)).map((step, index) => (
                              <HStack key={index} spacing={2} align="start">
                                <Circle size="16px" bg="orange.400" color="white" fontWeight="bold" fontSize="xs" flexShrink={0}>
                                  {index + 1}
                                </Circle>
                                <Text fontSize="xs" lineHeight="1.4" color="gray.600">
                                  {step.length > 50 ? step.substring(0, 50) + '...' : step}
                                </Text>
                              </HStack>
                            ))}
                            {recipe.instructions.length > 2 && (
                              <Button 
                                variant="ghost" 
                                size="sm" 
                                fontSize="xs" 
                                color="orange.600"
                                _hover={{ bg: "orange.50" }}
                                onClick={() => setShowAllInstructions(!showAllInstructions)}
                              >
                                {showAllInstructions 
                                  ? "접기" 
                                  : `+${recipe.instructions.length - 2}단계 더보기`
                                }
                              </Button>
                            )}
                          </VStack>
                        </Box>
                      </VStack>
                    </VStack>
                  </AccordionPanel>
                </AccordionItem>
              </Accordion>
            </VStack>
          </HStack>
        </CardBody>
      </Card>
    );
  };

  return (
    <Layout>
      <Container maxW="6xl" py={8}>
        <VStack spacing={8} align="stretch">
          {/* 헤더 */}
          <Card bg="white" borderRadius="2xl" shadow="lg" border="1px" borderColor="gray.200">
            <CardBody p={6}>
              <HStack justify="space-between" align="center">
                <HStack spacing={4}>
                  <Button 
                    leftIcon={<ArrowBackIcon />} 
                    variant="ghost" 
                    colorScheme="gray"
                    onClick={handleBackToInput}
                  >
                    다시 입력하기
                  </Button>
                  <VStack align="start" spacing={1}>
                    <Heading size="lg" color="gray.800">
                      🍳 {userProfile.goal === 'weight_loss' ? '다이어트' : 
                          userProfile.goal === 'muscle_gain' ? '근성장' : '건강 유지'} 맞춤 레시피
                    </Heading>
                    <Text fontSize="sm" color="gray.600">
                      목표에 최적화된 맛있는 요리 레시피를 추천드려요
                    </Text>
                  </VStack>
                </HStack>
              </HStack>
            </CardBody>
          </Card>





          {/* 🍳 레시피 섹션 연결 헤더 */}
          <Box textAlign="center" py={4}>
            <VStack spacing={3}>
              <HStack spacing={3} justify="center">
                <Box w="50px" h="1px" bg="blue.300"></Box>
                <Text fontSize="2xl">🍽️</Text>
                <Box w="50px" h="1px" bg="blue.300"></Box>
              </HStack>
              <Heading size="lg" color="gray.700">
                🎯 {userProfile.goal === 'weight_loss' ? '다이어트' : 
                    userProfile.goal === 'muscle_gain' ? '근성장' : '건강 유지'} 맞춤 레시피
              </Heading>
              {isLoadingRecipes && (
                <HStack spacing={2}>
                  <Progress size="xs" isIndeterminate colorScheme="blue" w="200px" />
                  <Text fontSize="sm" color="gray.600">만개의 레시피 로딩 중...</Text>
                </HStack>
              )}
              <Text fontSize="sm" color="gray.600" maxW="500px">
                위 예산과 영양 목표에 맞춰 선별된 레시피들입니다. 각 레시피는 구매 가능 횟수와 1회당 비용이 계산되어 있어요!
              </Text>
            </VStack>
          </Box>



          {/* 🍳 컴팩트 레시피 탭 */}
          <Card bg="gradient-to-r from-teal.50 to-green.50" borderRadius="xl" overflow="hidden">
            <CardBody p={6}>
              <VStack spacing={4} textAlign="center" mb={6}>
                <Text fontSize="lg" fontWeight="bold" color="teal.700">
                  🎯 {userProfile.goal === 'weight_loss' ? '다이어트' : 
                      userProfile.goal === 'muscle_gain' ? '근성장' : '건강 유지'} 목표에 
                  최적화된 맞춤 레시피
                </Text>
                <Text color="gray.600" fontSize="sm">
                  각 요리를 클릭하면 상세 조리법과 재료 정보를 확인할 수 있어요
                </Text>
              </VStack>
            </CardBody>
          </Card>

          <Tabs isFitted variant="soft-rounded" colorScheme="teal" bg="white" p={4} borderRadius="xl" shadow="sm">
            <TabList mb="1em" bg="gray.50" borderRadius="lg" p={1}>
              <Tab _selected={{ bg: "white", shadow: "sm" }}>🌅 아침</Tab>
              <Tab _selected={{ bg: "white", shadow: "sm" }}>☀️ 점심</Tab>
              <Tab _selected={{ bg: "white", shadow: "sm" }}>🌙 저녁</Tab>
            </TabList>

            <TabPanels>
              {/* 아침 식사 */}
              <TabPanel>
                <VStack spacing={3} align="stretch">
                  {mealTypeRecipes.breakfast.map((recipe) => (
                    <CompactRecipeCard key={recipe.id} recipe={recipe} />
                  ))}
                </VStack>
              </TabPanel>

              {/* 점심 식사 */}
              <TabPanel>
                <VStack spacing={3} align="stretch">
                  {mealTypeRecipes.lunch.map((recipe) => (
                    <CompactRecipeCard key={recipe.id} recipe={recipe} />
                  ))}
                </VStack>
              </TabPanel>

              {/* 저녁 식사 */}
              <TabPanel>
                <VStack spacing={3} align="stretch">
                  {mealTypeRecipes.dinner.map((recipe) => (
                    <CompactRecipeCard key={recipe.id} recipe={recipe} />
                  ))}
                </VStack>
              </TabPanel>
            </TabPanels>
          </Tabs>

          {/* 하단 액션 버튼 */}
          <HStack spacing={4} justify="center">
            <Button 
              colorScheme="gray" 
              variant="outline" 
              size="lg" 
              onClick={handleBackToInput} 
              leftIcon={<ArrowBackIcon />}
            >
              다시 입력하기
            </Button>
          </HStack>


        </VStack>
      </Container>
    </Layout>
  );
};

export default RecommendationScreen;
