import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  VStack,
  HStack,
  Text,
  Button,
  Card,
  CardBody,
  Heading,
  Image,
  Badge,
  useToast,
  Collapse,
  Icon,
} from '@chakra-ui/react';
import { ArrowBackIcon, ExternalLinkIcon, TimeIcon, InfoIcon, ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import { useAppContext } from '../context/AppContext';
import { CoupangProduct, Recipe } from '../types';
import { getRecipesByGoal, getRecipeIngredients, recipeLoader } from '../data/recipeData';
import { calculateRecipeFrequency } from '../utils/personalizedRecommendation';
import { debugRecipeMapping } from '../utils/recipeDebugger';

import Layout from './Layout/Layout';

const RecommendationScreen: React.FC = () => {
  const navigate = useNavigate();
  const toast = useToast();
  const {
    userInput,
    recommendationResult,
    setSelectedProduct,
    userProfile // ì‚¬ìš©ìž ëª©í‘œ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
  } = useAppContext();

  // ðŸ³ ì‚¬ìš©ìž ëª©í‘œì— ë§žëŠ” ë ˆì‹œí”¼ ê°€ì ¸ì˜¤ê¸° (ë¹„ë™ê¸°) - hooksë¥¼ ì¡°ê±´ë¶€ ë¦¬í„´ ì „ì— ì„ ì–¸
  const [recommendedRecipes, setRecommendedRecipes] = useState<Recipe[]>([]);
  const [isLoadingRecipes, setIsLoadingRecipes] = useState(true);
  const [recipeStats, setRecipeStats] = useState<any>(null);

  useEffect(() => {
    // ì¶”ì²œ ê²°ê³¼ê°€ ì—†ìœ¼ë©´ ë©”ì¸ íŽ˜ì´ì§€ë¡œ ë¦¬ë‹¤ì´ë ‰íŠ¸
    if (!recommendationResult) {
      toast({
        title: "ì¶”ì²œ ê²°ê³¼ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤",
        description: "ë©”ì¸ íŽ˜ì´ì§€ë¡œ ëŒì•„ê°€ì„œ ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
      navigate('/');
      return;
    }

    const loadRecipes = async () => {
      setIsLoadingRecipes(true);
      try {
        // ðŸ” ë””ë²„ê¹… ì‹¤í–‰
        console.log('ðŸ” === ë ˆì‹œí”¼ ë§¤í•‘ ë””ë²„ê¹… ì‹œìž‘ ===');
        debugRecipeMapping();

        // ìºì‹œ ì§„ë‹¨
        console.log('ðŸ” === ìºì‹œ ì§„ë‹¨ ì‹œìž‘ ===');
        await recipeLoader.diagnoseCacheStatus();

        // ì‚¬ìš©ìž ëª©í‘œ í™•ì¸ ë° ì„¤ì •
        const userGoal = userProfile.goal || 'maintenance';
        console.log('ðŸŽ¯ ì‚¬ìš©ìž ëª©í‘œ:', userGoal);

        // ëª©í‘œê°€ ì—†ìœ¼ë©´ ê¸°ë³¸ê°’ìœ¼ë¡œ ì„¤ì •
        if (!userProfile.goal) {
          console.log('âš ï¸ ì‚¬ìš©ìž ëª©í‘œê°€ ì„¤ì •ë˜ì§€ ì•ŠìŒ, ê¸°ë³¸ê°’(maintenance)ìœ¼ë¡œ ì„¤ì •');
        }

        // ë ˆì‹œí”¼ ë¡œë“œ
        const recipes = await getRecipesByGoal(userGoal);
        console.log('ðŸ“‹ ë¡œë“œëœ ë ˆì‹œí”¼ ìˆ˜:', recipes.length);
        console.log('ðŸ“‹ ë ˆì‹œí”¼ IDë“¤:', recipes.map(r => r.id));

        if (recipes.length === 0) {
