import React, { useState } from 'react';
import {
  VStack,
  HStack,
  Text,
  Card,
  CardBody,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  FormControl,
  FormLabel,
  Switch,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  Alert,
  AlertIcon,
  Box,
  Progress,
  Badge,
  Divider,
} from '@chakra-ui/react';
import { CalorieCalculation } from '../../types';
import { getGoalDescription } from '../../utils/calorieCalculator';

interface CalorieReviewProps {
  calculation: CalorieCalculation;
  userGoal: 'weight_loss' | 'maintenance' | 'muscle_gain';
  onCaloriesChange: (calories: number, useCustom: boolean) => void;
}

const CalorieReview: React.FC<CalorieReviewProps> = ({
  calculation,
  userGoal,
  onCaloriesChange,
}) => {
  const [useCustomCalories, setUseCustomCalories] = useState(false);
  const [customCalories, setCustomCalories] = useState(calculation.targetCalories);

  const handleCustomToggle = (enabled: boolean) => {
    setUseCustomCalories(enabled);
    onCaloriesChange(
      enabled ? customCalories : calculation.targetCalories,
      enabled
    );
  };

  const handleCustomCaloriesChange = (value: number) => {
    setCustomCalories(value);
    if (useCustomCalories) {
      onCaloriesChange(value, true);
    }
  };

  const getGoalColor = (goal: string) => {
    switch (goal) {
      case 'weight_loss': return 'red';
      case 'maintenance': return 'green';
      case 'muscle_gain': return 'blue';
      default: return 'gray';
    }
  };

  const getGoalEmoji = (goal: string) => {
    switch (goal) {
      case 'weight_loss': return 'üî•';
      case 'maintenance': return '‚öñÔ∏è';
      case 'muscle_gain': return 'üí™';
      default: return 'üéØ';
    }
  };

  const currentCalories = useCustomCalories ? customCalories : calculation.targetCalories;
  const caloriesDiff = currentCalories - calculation.tdee;
  const percentDiff = Math.round((caloriesDiff / calculation.tdee) * 100);

  return (
    <VStack spacing={6} align="stretch">
      <VStack spacing={2} textAlign="center">
        <Text fontSize="4xl">üìä</Text>
        <Text fontSize="2xl" fontWeight="bold" color="gray.800">
          ÎßûÏ∂§ ÏπºÎ°úÎ¶¨Í∞Ä Í≥ÑÏÇ∞ÎêòÏóàÏñ¥Ïöî!
        </Text>
        <Text color="gray.600">
          Í≥ºÌïôÏ†Å Í≥µÏãùÏùÑ Î∞îÌÉïÏúºÎ°ú Í≥ÑÏÇ∞Îêú Í∞úÏù∏ ÎßûÏ∂§ ÏπºÎ°úÎ¶¨ÏûÖÎãàÎã§
        </Text>
      </VStack>

      {/* Í≥ÑÏÇ∞ Í≤∞Í≥º ÏöîÏïΩ */}
      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={4}>
        <Card>
          <CardBody textAlign="center">
            <Stat>
              <StatLabel>Í∏∞Ï¥àÎåÄÏÇ¨Ïú® (BMR)</StatLabel>
              <StatNumber color="blue.500">{Math.round(calculation.bmr)}</StatNumber>
              <StatHelpText>kcal/Ïùº</StatHelpText>
            </Stat>
            <Text fontSize="xs" color="gray.500" mt={2}>
              ÏÉùÏ°¥Ïóê ÌïÑÏöîÌïú ÏµúÏÜå ÏπºÎ°úÎ¶¨
            </Text>
          </CardBody>
        </Card>

        <Card>
          <CardBody textAlign="center">
            <Stat>
              <StatLabel>Ï¥ù ÏÜåÎ™® ÏπºÎ°úÎ¶¨ (TDEE)</StatLabel>
              <StatNumber color="orange.500">{calculation.tdee}</StatNumber>
              <StatHelpText>kcal/Ïùº</StatHelpText>
            </Stat>
            <Text fontSize="xs" color="gray.500" mt={2}>
              ÌôúÎèôÎüâ Ìè¨Ìï® ÏùºÏùº ÏÜåÎ™®Îüâ
            </Text>
          </CardBody>
        </Card>

        <Card bg={`${getGoalColor(userGoal)}.50`} borderColor={`${getGoalColor(userGoal)}.200`}>
          <CardBody textAlign="center">
            <Stat>
              <StatLabel>Î™©Ìëú ÏπºÎ°úÎ¶¨</StatLabel>
              <StatNumber color={`${getGoalColor(userGoal)}.600`}>
                {currentCalories}
              </StatNumber>
              <StatHelpText>
                {percentDiff > 0 ? '+' : ''}{percentDiff}% 
                <Badge ml={1} colorScheme={getGoalColor(userGoal)} size="sm">
                  {getGoalEmoji(userGoal)}
                </Badge>
              </StatHelpText>
            </Stat>
            <Text fontSize="xs" color="gray.600" mt={2}>
              {getGoalDescription(userGoal)}
            </Text>
          </CardBody>
        </Card>
      </SimpleGrid>

      {/* ÏòÅÏñëÏÜå ÎπÑÏú® */}
      <Card>
        <CardBody>
          <VStack spacing={4}>
            <Text fontSize="lg" fontWeight="bold" color="teal.700">
              ü•ó ÏµúÏ†Å ÏòÅÏñëÏÜå ÎπÑÏú®
            </Text>

            <SimpleGrid columns={3} spacing={4} w="full">
              <VStack>
                <Text fontSize="sm" color="gray.600">ÌÉÑÏàòÌôîÎ¨º</Text>
                <Text fontSize="2xl" fontWeight="bold" color="orange.500">
                  {calculation.macros.carb}%
                </Text>
                <Progress 
                  value={calculation.macros.carb} 
                  colorScheme="orange" 
                  size="sm" 
                  w="full"
                />
                <Text fontSize="xs" color="gray.500">
                  {Math.round(currentCalories * calculation.macros.carb / 100 / 4)}g
                </Text>
              </VStack>

              <VStack>
                <Text fontSize="sm" color="gray.600">Îã®Î∞±Ïßà</Text>
                <Text fontSize="2xl" fontWeight="bold" color="red.500">
                  {calculation.macros.protein}%
                </Text>
                <Progress 
                  value={calculation.macros.protein} 
                  colorScheme="red" 
                  size="sm" 
                  w="full"
                />
                <Text fontSize="xs" color="gray.500">
                  {Math.round(currentCalories * calculation.macros.protein / 100 / 4)}g
                </Text>
              </VStack>

              <VStack>
                <Text fontSize="sm" color="gray.600">ÏßÄÎ∞©</Text>
                <Text fontSize="2xl" fontWeight="bold" color="yellow.500">
                  {calculation.macros.fat}%
                </Text>
                <Progress 
                  value={calculation.macros.fat} 
                  colorScheme="yellow" 
                  size="sm" 
                  w="full"
                />
                <Text fontSize="xs" color="gray.500">
                  {Math.round(currentCalories * calculation.macros.fat / 100 / 9)}g
                </Text>
              </VStack>
            </SimpleGrid>
          </VStack>
        </CardBody>
      </Card>

      {/* ÏπºÎ°úÎ¶¨ Ïª§Ïä§ÌÑ∞ÎßàÏù¥Ïßï */}
      <Card>
        <CardBody>
          <VStack spacing={4} align="stretch">
            <HStack justify="space-between">
              <VStack align="start" spacing={1}>
                <Text fontWeight="bold">ÏßÅÏ†ë ÏπºÎ°úÎ¶¨ ÏÑ§Ï†ïÌïòÍ∏∞</Text>
                <Text fontSize="sm" color="gray.600">
                  Í∂åÏû• ÏπºÎ°úÎ¶¨Í∞Ä ÎßûÏßÄ ÏïäÏúºÎ©¥ ÏßÅÏ†ë Ï°∞Ï†ïÌï† Ïàò ÏûàÏñ¥Ïöî
                </Text>
              </VStack>
              <Switch
                size="lg"
                colorScheme="teal"
                isChecked={useCustomCalories}
                onChange={(e) => handleCustomToggle(e.target.checked)}
              />
            </HStack>

            {useCustomCalories && (
              <FormControl>
                <FormLabel>Î™©Ìëú ÏπºÎ°úÎ¶¨ (kcal)</FormLabel>
                <NumberInput
                  value={customCalories}
                  onChange={(_, value) => handleCustomCaloriesChange(isNaN(value) ? 0 : value)}
                  min={800}
                  max={5000}
                  step={50}
                  size="lg"
                >
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>

                {Math.abs(customCalories - calculation.targetCalories) > 300 && (
                  <Alert status="warning" mt={2} borderRadius="md">
                    <AlertIcon />
                    <Box>
                      <Text fontSize="sm">
                        Í∂åÏû• ÏπºÎ°úÎ¶¨ÏôÄ {Math.abs(customCalories - calculation.targetCalories)}kcal Ï∞®Ïù¥Í∞Ä ÎÇòÏöî.
                        ÎÑàÎ¨¥ Í∑πÎã®Ï†ÅÏù∏ ÏπºÎ°úÎ¶¨Îäî Í±¥Í∞ïÏóê Ï¢ãÏßÄ ÏïäÏùÑ Ïàò ÏûàÏñ¥Ïöî.
                      </Text>
                    </Box>
                  </Alert>
                )}
              </FormControl>
            )}
          </VStack>
        </CardBody>
      </Card>

      <Divider />

      <Alert status="info" borderRadius="md">
        <AlertIcon />
        <Box>
          <Text fontSize="sm">
            <strong>üí° ÏïåÏïÑÎëêÏÑ∏Ïöî!</strong> Í≥ÑÏÇ∞Îêú ÏπºÎ°úÎ¶¨Îäî ÏùºÎ∞òÏ†ÅÏù∏ Í≥µÏãùÏùÑ Î∞îÌÉïÏúºÎ°ú Ìïú Ï∂îÏ†ïÏπòÏòàÏöî. 
            Í∞úÏù∏Ï∞®Í∞Ä ÏûàÏùÑ Ïàò ÏûàÏúºÎãà 2-3Ï£º ÏÇ¨Ïö©Ìï¥Î≥¥ÏãúÍ≥† ÌïÑÏöîÏóê Îî∞Îùº Ï°∞Ï†ïÌï¥Ï£ºÏÑ∏Ïöî.
          </Text>
        </Box>
      </Alert>
    </VStack>
  );
};

export default CalorieReview;
