import React from 'react';
import {
  VStack,
  HStack,
  Card,
  CardBody,
  Heading,
  Text,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  Button,
  Select,
  FormControl,
  FormLabel,
  SimpleGrid,
} from '@chakra-ui/react';
import { UserProfile } from '../../types';

interface BasicInfoProps {
  userProfile: Partial<UserProfile>;
  onProfileUpdate: (update: Partial<UserProfile>) => void;
}

const BasicInfo: React.FC<BasicInfoProps> = ({ userProfile, onProfileUpdate }) => {
  return (
    <Card borderRadius="2xl" shadow="lg">
      <CardBody p={8}>
        <VStack spacing={6} align="stretch">
          <VStack spacing={2} textAlign="center">
            <Text fontSize="4xl">üë§</Text>
            <Heading size="lg" color="gray.800">Í∏∞Î≥∏ Ï†ïÎ≥¥</Heading>
            <Text color="gray.600" fontSize="sm">
              Ï†ïÌôïÌïú ÏãùÎã® Ï∂îÏ≤úÏùÑ ÏúÑÌï¥ Í∏∞Î≥∏ Ï†ïÎ≥¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî
            </Text>
          </VStack>

          <SimpleGrid columns={{ base: 1, md: 2 }} spacing={6}>
            {/* ÏÑ±Î≥Ñ */}
            <FormControl>
              <FormLabel fontSize="sm" fontWeight="bold" color="gray.700">ÏÑ±Î≥Ñ</FormLabel>
              <HStack spacing={3}>
                <Button
                  flex={1}
                  variant={userProfile.gender === 'male' ? 'solid' : 'outline'}
                  colorScheme="teal"
                  onClick={() => onProfileUpdate({ gender: 'male' })}
                  size="lg"
                >
                  üë® ÎÇ®ÏÑ±
                </Button>
                <Button
                  flex={1}
                  variant={userProfile.gender === 'female' ? 'solid' : 'outline'}
                  colorScheme="teal"
                  onClick={() => onProfileUpdate({ gender: 'female' })}
                  size="lg"
                >
                  üë© Ïó¨ÏÑ±
                </Button>
              </HStack>
            </FormControl>

            {/* ÎÇòÏù¥ */}
            <FormControl>
              <FormLabel fontSize="sm" fontWeight="bold" color="gray.700">ÎÇòÏù¥</FormLabel>
              <NumberInput
                value={userProfile.age || 25}
                onChange={(value) => onProfileUpdate({ age: parseInt(value) || 25 })}
                min={15}
                max={80}
                size="lg"
              >
                <NumberInputField placeholder="ÎÇòÏù¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
                <NumberInputStepper>
                  <NumberIncrementStepper />
                  <NumberDecrementStepper />
                </NumberInputStepper>
              </NumberInput>
            </FormControl>

            {/* ÌÇ§ */}
            <FormControl>
              <FormLabel fontSize="sm" fontWeight="bold" color="gray.700">ÌÇ§ (cm)</FormLabel>
              <NumberInput
                value={userProfile.height || 170}
                onChange={(value) => onProfileUpdate({ height: parseInt(value) || 170 })}
                min={140}
                max={220}
                size="lg"
              >
                <NumberInputField placeholder="ÌÇ§Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
                <NumberInputStepper>
                  <NumberIncrementStepper />
                  <NumberDecrementStepper />
                </NumberInputStepper>
              </NumberInput>
            </FormControl>

            {/* Î™∏Î¨¥Í≤å */}
            <FormControl>
              <FormLabel fontSize="sm" fontWeight="bold" color="gray.700">Î™∏Î¨¥Í≤å (kg)</FormLabel>
              <NumberInput
                value={userProfile.weight || 70}
                onChange={(value) => onProfileUpdate({ weight: parseInt(value) || 70 })}
                min={40}
                max={150}
                size="lg"
              >
                <NumberInputField placeholder="Î™∏Î¨¥Í≤åÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
                <NumberInputStepper>
                  <NumberIncrementStepper />
                  <NumberDecrementStepper />
                </NumberInputStepper>
              </NumberInput>
            </FormControl>
          </SimpleGrid>

          {/* ÌôúÎèôÎüâ (Í∞ÑÏÜåÌôî) */}
          <FormControl>
            <FormLabel fontSize="sm" fontWeight="bold" color="gray.700">ÌôúÎèôÎüâ</FormLabel>
            <Select
              value={userProfile.activityLevel || 'moderately_active'}
              onChange={(e) => onProfileUpdate({ 
                activityLevel: e.target.value as UserProfile['activityLevel'] 
              })}
              size="lg"
              placeholder="ÌôúÎèôÎüâÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî"
            >
              <option value="sedentary">üõãÔ∏è Ïö¥Îèô Í±∞Ïùò ÏïàÌï® (ÏÇ¨Î¨¥ÏßÅ)</option>
              <option value="lightly_active">üö∂‚Äç‚ôÇÔ∏è Í∞ÄÎ≤ºÏö¥ ÌôúÎèô (Ï£º 1-3Ìöå Ïö¥Îèô)</option>
              <option value="moderately_active">üèÉ‚Äç‚ôÇÔ∏è Î≥¥ÌÜµ ÌôúÎèô (Ï£º 3-5Ìöå Ïö¥Îèô)</option>
              <option value="very_active">üí™ ÌôúÎ∞úÌïú ÌôúÎèô (Ï£º 6-7Ìöå Ïö¥Îèô)</option>
              <option value="extremely_active">üèãÔ∏è‚Äç‚ôÇÔ∏è Îß§Ïö∞ ÌôúÎ∞ú (ÌïòÎ£® 2Ìöå Ïù¥ÏÉÅ Ïö¥Îèô)</option>
            </Select>
          </FormControl>

          {/* ÏïàÎÇ¥ Î©îÏãúÏßÄ */}
          <Card bg="blue.50" borderRadius="lg">
            <CardBody p={4}>
              <Text fontSize="sm" color="blue.700" textAlign="center">
                üí° <strong>Ï†ïÌôïÌïú Ï†ïÎ≥¥ÏùºÏàòÎ°ù Îçî ÎßûÏ∂§ÌôîÎêú ÏãùÎã®ÏùÑ Ï∂îÏ≤úÎ∞õÏùÑ Ïàò ÏûàÏñ¥Ïöî!</strong>
              </Text>
            </CardBody>
          </Card>
        </VStack>
      </CardBody>
    </Card>
  );
};

export default BasicInfo;
