  // Ï∂îÏ≤ú ÏòàÏÇ∞ ÏòµÏÖòÎì§
  const budgetOptions = [
    {
      value: 130000,
      label: '13ÎßåÏõê',
      subtitle: 'Ï†àÏïΩÌòï',
      description: 'ÌïúÎÅº 6Ï≤úÏõêÎåÄÎ°ú ÏïåÎú∞ÌïòÍ≤å Íµ¨ÏÑ±Ìïú ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['Í∏∞Î≥∏ Îã®Î∞±Ïßà(Îã¨Í±Ä,Îã≠Í∞ÄÏä¥ÏÇ¥)', 'Ï†úÏ≤† Ï±ÑÏÜåÎ•ò', 'ÏåÄ/Î©¥Î•ò Ï§ëÏã¨'],
      color: 'blue',
      recommended: false,
    },
    {
      value: 170000,
      label: '17ÎßåÏõê',
      subtitle: 'ÌëúÏ§ÄÌòï',
      description: 'ÌïúÎÅº 8Ï≤úÏõêÎåÄÎ°ú Í∑†ÌòïÏû°Ìûå ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['Îã§ÏñëÌïú Ïú°Î•ò/ÏÉùÏÑ†', 'Ïã†ÏÑ†Ìïú Ï±ÑÏÜå/Í≥ºÏùº', 'Ïú†Ï†úÌíà Ìè¨Ìï®'],
      color: 'green',
      recommended: true,
    },
    {
      value: 210000,
      label: '21ÎßåÏõê',
      subtitle: 'ÌíçÏÑ±Ìòï',
      description: 'ÌïúÎÅº 1ÎßåÏõêÎåÄÎ°ú ÌíçÏÑ±Ìïú ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['ÌîÑÎ¶¨ÎØ∏ÏóÑ Ïú°Î•ò', 'ÏàòÏûÖ Í≥ºÏùº', 'Í≤¨Í≥ºÎ•ò/Í±¥Í∞ïÏãùÌíà'],
      color: 'orange',
      recommended: false,
    },
    {
      value: 250000,
      label: '25ÎßåÏõê',
      subtitle: 'ÌîÑÎ¶¨ÎØ∏ÏóÑ',
      description: 'ÌïúÎÅº 12Ï≤úÏõêÎåÄÎ°ú ÏµúÍ≥†Í∏â ÏùºÏ£ºÏùº ÏãùÎã®',
      features: ['ÏµúÍ≥†Í∏â ÏãùÏû¨Î£å', 'Ïò§Í∞ÄÎãâ Ï†úÌíà', 'Í≥†Í∏â Í±¥Í∞ïÎ≥¥Ï°∞ÏãùÌíà'],
      color: 'purple',
      recommended: false,
    },
  ];

  const handlePresetSelect = (budget: number) => {
    setUseCustom(false);
    setCustomBudget(budget);
    onBudgetSelect(budget);
  };

  const handleCustomBudgetChange = (value: number) => {
    setCustomBudget(value);
    if (useCustom) {
      onBudgetSelect(value);
    }
  };

  const handleCustomToggle = () => {
    const newUseCustom = !useCustom;
    setUseCustom(newUseCustom);
    if (newUseCustom) {
      onBudgetSelect(customBudget);
